// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Layout from "./components/Layout";
import ProtectedRoute from "./components/ProtectedRoute";
import DashboardAdmin from "./pages/DashboardAdmin";
import DashboardUser from "./pages/DashboardUser";
import Rekap from "./pages/Rekap";
import Login from "./pages/Login";
import Capture from "./pages/Capture";
import "./App.css";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [absensiData, setAbsensiData] = useState([]);

  // login via backend
  const login = async (username, password) => {
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      if (res.ok) {
        setCurrentUser(data.user);
        // fetch absensi user
        fetchUserAbsensi(data.user.id);
        return true;
      } else {
        alert(data.error);
        return false;
      }
    } catch (err) {
      console.error(err);
      alert("Tidak bisa konek ke server");
      return false;
    }
  };

  const logout = () => {
    setCurrentUser(null);
    setAbsensiData([]);
  };

  const fetchUserAbsensi = async (userId) => {
    try {
      const res = await fetch(`http://localhost:5000/absensi/${userId}`);
      const data = await res.json();
      if (res.ok) setAbsensiData(data);
      else console.error(data);
    } catch (err) {
      console.error(err);
    }
  };

  // dipanggil dari Capture setelah sukses POST
  const addAbsensi = (absensi) => {
    // optional: push ke state agar langsung tampil
    setAbsensiData(prev => [{ id: Date.now(), ...absensi }, ...prev]);
  };

  // jika user sudah login dan reload page, kita tidak maintain session server-side di contoh ini.
  // (bisa ditingkatkan pakai JWT / session)
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login onLogin={login} />} />
        <Route
          path="/"
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route
            index
            element={
              currentUser?.role === "admin" ? (
                <DashboardAdmin currentUser={currentUser} />
              ) : (
                <DashboardUser currentUser={currentUser} absensiData={absensiData} />
              )
            }
          />
          <Route path="/rekap" element={<Rekap />} />

          <Route
            path="/capture"
            element={
              <ProtectedRoute currentUser={currentUser}>
                <Layout currentUser={currentUser} onLogout={logout}>
                  <Capture currentUser={currentUser} onSuccess={addAbsensi} />
                </Layout>
              </ProtectedRoute>
            }
          />
        </Route>

        {/* default route */}
        <Route path="*" element={<Navigate to={currentUser ? "/" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
