import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

const Login = ({ onLogin, currentUser, onSignUp, dummyUsers }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isSignUp, setIsSignUp] = useState(false);
  const [signUpData, setSignUpData] = useState({
    username: '',
    password: '',
    name: '',
    role: 'user'
  });

  if (currentUser) {
    return <Navigate to="/" replace />;
  }

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSignUpChange = (e) => {
    setSignUpData({
      ...signUpData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onLogin(formData.username, formData.password)) {
      setError('');
    } else {
      setError('Username atau password salah!');
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (!signUpData.username || !signUpData.password || !signUpData.name) {
      setError('Semua field harus diisi');
      return;
    }
    if (dummyUsers.some(u => u.username === signUpData.username)) {
      setError('Username sudah digunakan');
      return;
    }
    onSignUp(signUpData);
    setIsSignUp(false);
    setFormData({ username: signUpData.username, password: signUpData.password });
    setError('');
  };

  return (
    <div className="login-container">
      {!isSignUp ? (
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>üîê Login Sistem Absensi</h2>
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              placeholder="Masukkan username"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Masukkan password"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Login
          </button>
          <button type="button" className="btn btn-secondary" style={{ marginLeft: '10px' }} onClick={() => { setIsSignUp(true); setError(''); }}>
            Sign Up
          </button>
        </form>
      ) : (
        <form className="login-form" onSubmit={handleSignUp}>
          <h2>üìù Sign Up User Baru</h2>
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={signUpData.username}
              onChange={handleSignUpChange}
              required
              placeholder="Username baru"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={signUpData.password}
              onChange={handleSignUpChange}
              required
              placeholder="Password"
            />
          </div>
          <div className="form-group">
            <label>Nama Lengkap:</label>
            <input
              type="text"
              name="name"
              value={signUpData.name}
              onChange={handleSignUpChange}
              required
              placeholder="Nama lengkap"
            />
          </div>
          <div className="form-group">
            <label>Role:</label>
            <select
              name="role"
              value={signUpData.role}
              onChange={handleSignUpChange}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button type="submit" className="btn btn-success">
            Daftar
          </button>
          <button type="button" className="btn btn-secondary" style={{ marginLeft: '10px' }} onClick={() => { setIsSignUp(false); setError(''); }}>
            Kembali ke Login
          </button>
        </form>
      )}
    </div>
  );
};

export default Login;
