import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import ProtectedRoute from './components/ProtectedRoute';
import DashboardAdmin from './pages/DashboardAdmin';
import DashboardUser from './pages/DashboardUser';
import Rekap from './pages/Rekap';
import Login from './pages/Login';
import Capture from './pages/Capture';
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(
    JSON.parse(localStorage.getItem("user")) || null
  );
  const [absensiData, setAbsensiData] = useState([]);

  // üîë LOGIN ke backend
  const login = async (username, password) => {
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await res.json();
      if (res.ok) {
        setCurrentUser(data.user);
        localStorage.setItem("user", JSON.stringify(data.user)); // simpan biar bisa dipakai terus
        return true;
      } else {
        alert("Login gagal: " + data.error);
        return false;
      }
    } catch (err) {
      console.error(err);
      return false;
    }
  };

  // ‚úçÔ∏è SIGN UP
  const signUp = async (newUser) => {
    try {
      const res = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      const data = await res.json();
      if (res.ok) {
        alert("Pendaftaran berhasil!");
        return true;
      } else {
        alert("Gagal daftar: " + data.error);
        return false;
      }
    } catch (err) {
      console.error(err);
      return false;
    }
  };

  const logout = () => {
    setCurrentUser(null);
    localStorage.removeItem("user");
  };

  // üìù ABSENSI
  const addAbsensi = async (absensi) => {
    try {
      const res = await fetch("http://localhost:5000/absensi", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: currentUser.id,
          status: absensi.status,
          keterangan: absensi.keterangan,
          foto: absensi.foto,
        }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("‚úÖ " + data.message);
        setAbsensiData((prev) => [...prev, data.absensi]); // update state
      } else {
        alert("‚ùå Gagal absen: " + data.error);
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå Error koneksi server");
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            <Login
              onLogin={login}
              currentUser={currentUser}
              onSignUp={signUp}
            />
          }
        />
        <Route
          path="/"
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route
            index
            element={
              currentUser?.role === "admin" ? (
                <DashboardAdmin absensiData={absensiData} />
              ) : (
                <DashboardUser
                  currentUser={currentUser}
                  absensiData={absensiData}
                  onAddAbsensi={addAbsensi}
                />
              )
            }
          />
          <Route
            path="/rekap"
            element={<Rekap absensiData={absensiData} />}
          />
          <Route
            path="/capture"
            element={
              <ProtectedRoute currentUser={currentUser}>
                <Layout currentUser={currentUser} onLogout={logout}>
                  <Capture currentUser={currentUser} onAddAbsensi={addAbsensi} />
                </Layout>
              </ProtectedRoute>
            }
          />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
