import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData }) => {
  const [keterangan, setKeterangan] = useState('');
  const [fotoMasuk, setFotoMasuk] = useState(null);
  const [fotoPulang, setFotoPulang] = useState(null);
  const [showCameraMasuk, setShowCameraMasuk] = useState(false);
  const [showCameraPulang, setShowCameraPulang] = useState(false);
  const webcamRefMasuk = useRef(null);
  const webcamRefPulang = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // 📸 Foto Masuk
  const capturePhotoMasuk = () => {
    const imageSrc = webcamRefMasuk.current.getScreenshot();
    setFotoMasuk(imageSrc);
    setShowCameraMasuk(false);
  };

  // 📸 Foto Pulang
  const capturePhotoPulang = () => {
    const imageSrc = webcamRefPulang.current.getScreenshot();
    setFotoPulang(imageSrc);
    setShowCameraPulang(false);
  };

  const handleAbsensiMasuk = async (status) => {
    const response = await fetch("http://localhost:5001/absensi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,
        status,
        keterangan,
        foto: fotoMasuk
      }),
    });

    const data = await response.json();
    alert(response.ok ? "✅ " + data.message : "❌ " + data.error);
  };

  const handleAbsensiPulang = async () => {
    const response = await fetch("http://localhost:5001/absensi/pulang", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,
        status: "pulang",
        keterangan,
        foto: fotoPulang
      }),
    });

    const data = await response.json();
    alert(response.ok ? "✅ " + data.message : "❌ " + data.error);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      {/* ================= GRID 2 KOLOM ================= */}
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
        
        {/* ======= ABSEN MASUK ======= */}
        <div className="card">
          <h3>🕘 Absensi Masuk</h3>

          {/* Kamera Masuk */}
          <div className="form-group" style={{ textAlign: "center" }}>
            {!fotoMasuk && !showCameraMasuk && (
              <button onClick={() => setShowCameraMasuk(true)} className="btn btn-primary">
                📷 Buka Kamera Masuk
              </button>
            )}

            {showCameraMasuk && (
              <div>
                <Webcam
                  audio={false}
                  ref={webcamRefMasuk}
                  screenshotFormat="image/jpeg"
                  width={280}
                  videoConstraints={{ facingMode: "user" }}
                />
                <br />
                <button onClick={capturePhotoMasuk} className="btn btn-success">
                  📸 Ambil Foto
                </button>
              </div>
            )}

            {fotoMasuk && (
              <div>
                <img src={fotoMasuk} alt="Masuk" width={120} height={120} style={{ borderRadius: "8px" }} />
                <br />
                <button onClick={() => setFotoMasuk(null)} className="btn btn-secondary">
                  🔄 Ulangi Foto
                </button>
              </div>
            )}
          </div>

          {/* Tombol Absensi Masuk */}
          <div className="absensi-buttons">
            <button onClick={() => handleAbsensiMasuk("hadir")} className="btn btn-success">✅ HADIR</button>
            <button onClick={() => handleAbsensiMasuk("izin")} className="btn btn-warning">📝 IZIN</button>
            <button onClick={() => handleAbsensiMasuk("sakit")} className="btn btn-danger">🤒 SAKIT</button>
          </div>
        </div>

        {/* ======= ABSEN PULANG ======= */}
        <div className="card">
          <h3>🏠 Absensi Pulang</h3>

          {/* Kamera Pulang */}
          <div className="form-group" style={{ textAlign: "center" }}>
            {!fotoPulang && !showCameraPulang && (
              <button onClick={() => setShowCameraPulang(true)} className="btn btn-primary">
                📷 Buka Kamera Pulang
              </button>
            )}

            {showCameraPulang && (
              <div>
                <Webcam
                  audio={false}
                  ref={webcamRefPulang}
                  screenshotFormat="image/jpeg"
                  width={280}
                  videoConstraints={{ facingMode: "user" }}
                />
                <br />
                <button onClick={capturePhotoPulang} className="btn btn-success">
                  📸 Ambil Foto
                </button>
              </div>
            )}

            {fotoPulang && (
              <div>
                <img src={fotoPulang} alt="Pulang" width={120} height={120} style={{ borderRadius: "8px" }} />
                <br />
                <button onClick={() => setFotoPulang(null)} className="btn btn-secondary">
                  🔄 Ulangi Foto
                </button>
              </div>
            )}
          </div>

          {/* Tombol Absensi Pulang */}
          <div className="absensi-buttons">
            <button onClick={handleAbsensiPulang} className="btn btn-info">🏠 ABSEN PULANG</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
