import React from "react";

export default function DashboardUser({ currentUser, absensiData, onAddAbsensi }) {
  // Filter absensi user saat ini
  const userAbsensi = absensiData.filter(a => a.user_id === currentUser.id);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard {currentUser?.name || "User"}</h1>

      <section style={{ marginBottom: "30px" }}>
        <h2>Absensi Terbaru</h2>
        {userAbsensi.length === 0 ? (
          <p>Belum ada absensi.</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr style={{ borderBottom: "1px solid #ccc" }}>
                <th>Tanggal</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {userAbsensi
                .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
                .map((abs) => (
                  <tr key={abs.id} style={{ borderBottom: "1px solid #eee" }}>
                    <td>{abs.tanggal}</td>
                    <td>{abs.status}</td>
                    <td>{abs.keterangan || "-"}</td>
                    <td>
                      {abs.foto ? (
                        <img src={abs.foto} alt="absensi" style={{ width: "80px", borderRadius: "4px" }} />
                      ) : (
                        "-"
                      )}
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        )}
      </section>

      <section>
        <h2>Tambah Absensi</h2>
        <p>Gunakan menu <strong>Capture</strong> untuk mengirim absensi baru.</p>
      </section>
    </div>
  );
}
