import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [fotoMasuk, setFotoMasuk] = useState(null);
  const [fotoPulang, setFotoPulang] = useState(null);
  const [showCameraMasuk, setShowCameraMasuk] = useState(false);
  const [showCameraPulang, setShowCameraPulang] = useState(false);
  const [message, setMessage] = useState('');
  const webcamRefMasuk = useRef(null);
  const webcamRefPulang = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const todayAbsensi = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today
  );

  // üì∏ Ambil foto Masuk
  const capturePhotoMasuk = () => {
    const imageSrc = webcamRefMasuk.current.getScreenshot();
    setFotoMasuk(imageSrc);
    setShowCameraMasuk(false);
  };

  // üì∏ Ambil foto Pulang
  const capturePhotoPulang = () => {
    const imageSrc = webcamRefPulang.current.getScreenshot();
    setFotoPulang(imageSrc);
    setShowCameraPulang(false);
  };

  const handleAbsensiMasuk = async (status) => {
    const response = await fetch("http://localhost:5001/absensi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,
        status,
        keterangan,
        foto: fotoMasuk
      }),
    });

    const data = await response.json();
    if (response.ok) {
      alert("‚úÖ " + data.message);
    } else {
      alert("‚ùå Gagal absen masuk: " + data.error);
    }
  };

  const handleAbsensiPulang = async () => {
    const response = await fetch("http://localhost:5001/absensi/pulang", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,
        status: "pulang",
        keterangan,
        foto: fotoPulang
      }),
    });

    const data = await response.json();
    if (response.ok) {
      alert("‚úÖ " + data.message);
    } else {
      alert("‚ùå Gagal absen pulang: " + data.error);
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üëã Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>üìÖ {todayFormatted}</p>
      </div>

      {/* ================= TABEL MASUK ================= */}
      <div className="card">
        <h3>üïò Absensi Masuk</h3>
        <div className="form-group">
          <label>Keterangan (Opsional):</label>
          <textarea
            value={keterangan}
            onChange={(e) => setKeterangan(e.target.value)}
            placeholder="Masukkan keterangan jika diperlukan..."
          />
        </div>

        {/* Kamera Masuk */}
        <div className="form-group">
          {!fotoMasuk && !showCameraMasuk && (
            <button 
              onClick={() => setShowCameraMasuk(true)} 
              className="btn btn-primary"
            >
              üì∑ Buka Kamera Masuk
            </button>
          )}

          {showCameraMasuk && (
            <div style={{ marginTop: '10px' }}>
              <Webcam
                audio={false}
                ref={webcamRefMasuk}
                screenshotFormat="image/jpeg"
                width={300} // ukuran seragam
                videoConstraints={{ facingMode: "user" }}
              />
              <br />
              <button 
                onClick={capturePhotoMasuk} 
                className="btn btn-success"
              >
                üì∏ Ambil Foto Masuk
              </button>
            </div>
          )}

          {fotoMasuk && (
            <div style={{ marginTop: '10px' }}>
              <img src={fotoMasuk} alt="Foto Masuk" width={120} height={120} />
              <br />
              <button 
                onClick={() => setFotoMasuk(null)} 
                className="btn btn-secondary"
              >
                üîÑ Ulangi Foto
              </button>
            </div>
          )}
        </div>

        <div className="absensi-buttons">
          <button onClick={() => handleAbsensiMasuk('hadir')} className="btn btn-success">‚úÖ HADIR</button>
          <button onClick={() => handleAbsensiMasuk('izin')} className="btn btn-warning">üìù IZIN</button>
          <button onClick={() => handleAbsensiMasuk('sakit')} className="btn btn-danger">ü§í SAKIT</button>
        </div>
      </div>

      {/* ================= TABEL PULANG ================= */}
      <div className="card" style={{ marginTop: '30px' }}>
        <h3>üè† Absensi Pulang</h3>

        {/* Kamera Pulang */}
        <div className="form-group">
          {!fotoPulang && !showCameraPulang && (
            <button 
              onClick={() => setShowCameraPulang(true)} 
              className="btn btn-primary"
            >
              üì∑ Buka Kamera Pulang
            </button>
          )}

          {showCameraPulang && (
            <div style={{ marginTop: '10px' }}>
              <Webcam
                audio={false}
                ref={webcamRefPulang}
                screenshotFormat="image/jpeg"
                width={300} // ukuran seragam
                videoConstraints={{ facingMode: "user" }}
              />
              <br />
              <button 
                onClick={capturePhotoPulang} 
                className="btn btn-success"
              >
                üì∏ Ambil Foto Pulang
              </button>
            </div>
          )}

          {fotoPulang && (
            <div style={{ marginTop: '10px' }}>
              <img src={fotoPulang} alt="Foto Pulang" width={120} height={120} />
              <br />
              <button 
                onClick={() => setFotoPulang(null)} 
                className="btn btn-secondary"
              >
                üîÑ Ulangi Foto
              </button>
            </div>
          )}
        </div>

        <div className="absensi-buttons">
          <button onClick={handleAbsensiPulang} className="btn btn-info">üè† ABSEN PULANG</button>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
