import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const [jenisAbsensi, setJenisAbsensi] = useState('masuk');
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // Cek apakah sudah absen masuk hari ini
  const hasAbsensiMasukToday = absensiData.some(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'masuk'
  );

  // Cek apakah sudah absen pulang hari ini
  const hasAbsensiPulangToday = absensiData.some(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'pulang'
  );

  const todayAbsensiMasuk = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'masuk'
  );

  const todayAbsensiPulang = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'pulang'
  );

  // üì∏ Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false);
  };

  const handleAbsensi = async (status) => {
    // Validasi untuk absensi pulang
    if (jenisAbsensi === 'pulang' && !hasAbsensiMasukToday) {
      alert("‚ùå Anda harus absen masuk terlebih dahulu sebelum absen pulang");
      return;
    }

    // Validasi untuk mencegah duplikasi absensi
    if (jenisAbsensi === 'masuk' && hasAbsensiMasukToday) {
      alert("‚ùå Anda sudah absen masuk hari ini");
      return;
    }

    if (jenisAbsensi === 'pulang' && hasAbsensiPulangToday) {
      alert("‚ùå Anda sudah absen pulang hari ini");
      return;
    }

    const response = await fetch("http://localhost:5001/absensi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,
        status,
        keterangan,
        foto,
        jenis_absensi: jenisAbsensi
      }),
    });

    const data = await response.json();
    if (response.ok) {
      alert("‚úÖ " + data.message);
      // Reset form setelah berhasil
      setKeterangan('');
      setFoto(null);
      // Refresh data
      window.location.reload();
    } else {
      alert("‚ùå Gagal absen: " + data.error);
    }
  };

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.user_id === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üëã Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>üìÖ {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('‚ùå') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>‚úèÔ∏è Absensi Hari Ini</h3>
          
          {/* Status Absensi Hari Ini */}
          <div className="today-status" style={{ marginBottom: '20px', padding: '15px', background: '#f8f9fa', borderRadius: '8px' }}>
            <h4>Status Absensi Hari Ini:</h4>
            <div style={{ display: 'flex', gap: '20px', marginTop: '10px' }}>
              <div>
                <strong>Masuk:</strong> 
                {hasAbsensiMasukToday ? (
                  <span className={`status-badge status-${todayAbsensiMasuk?.status}`} style={{ marginLeft: '10px' }}>
                    {todayAbsensiMasuk?.status?.toUpperCase()}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
              <div>
                <strong>Pulang:</strong> 
                {hasAbsensiPulangToday ? (
                  <span className={`status-badge status-${todayAbsensiPulang?.status}`} style={{ marginLeft: '10px' }}>
                    {todayAbsensiPulang?.status?.toUpperCase()}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
            </div>
          </div>

          {hasAbsensiMasukToday && hasAbsensiPulangToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>‚úÖ Absensi Hari Ini Selesai</h2>
              <p>Anda sudah melakukan absensi masuk dan pulang hari ini.</p>
            </div>
          ) : (
            <div>
              {/* Kolom Masuk dan Pulang */}
              <div className="form-group">
                <label>Jenis Absensi:</label>
                <div className="absensi-type-buttons" style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('masuk')}
                    disabled={hasAbsensiMasukToday}
                    className={`btn ${jenisAbsensi === 'masuk' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'masuk' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'masuk' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'masuk' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: hasAbsensiMasukToday ? 'not-allowed' : 'pointer',
                      opacity: hasAbsensiMasukToday ? 0.6 : 1
                    }}
                  >
                    üè¢ Masuk {hasAbsensiMasukToday && '‚úì'}
                  </button>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('pulang')}
                    disabled={!hasAbsensiMasukToday || hasAbsensiPulangToday}
                    className={`btn ${jenisAbsensi === 'pulang' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'pulang' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'pulang' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'pulang' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: (!hasAbsensiMasukToday || hasAbsensiPulangToday) ? 'not-allowed' : 'pointer',
                      opacity: (!hasAbsensiMasukToday || hasAbsensiPulangToday) ? 0.6 : 1
                    }}
                  >
                    üè† Pulang {hasAbsensiPulangToday && '‚úì'}
                  </button>
                </div>
                <div style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
                  {!hasAbsensiMasukToday && jenisAbsensi === 'pulang' && 
                    "‚ö†Ô∏è Anda harus absen masuk terlebih dahulu sebelum absen pulang"}
                </div>
              </div>

              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    style={{ marginTop: '10px', background: 'blue', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                  >
                    üì∑ Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div style={{ marginTop: '10px' }}>
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                    >
                      üì∏ Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                    <br />
                    <button 
                      onClick={() => setFoto(null)} 
                      style={{ marginTop: '10px', background: 'gray', color: 'white', padding: '6px 12px', borderRadius: '5px' }}
                    >
                      üîÑ Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons">
                <button 
                  onClick={() => handleAbsensi('hadir')} 
                  className="btn btn-success"
                  disabled={jenisAbsensi === 'pulang' && !hasAbsensiMasukToday}
                >
                  ‚úÖ HADIR
                </button>
                <button 
                  onClick={() => handleAbsensi('izin')} 
                  className="btn btn-warning"
                  disabled={jenisAbsensi === 'pulang' && !hasAbsensiMasukToday}
                >
                  üìù IZIN
                </button>
                <button 
                  onClick={() => handleAbsensi('sakit')} 
                  className="btn btn-danger"
                  disabled={jenisAbsensi === 'pulang' && !hasAbsensiMasukToday}
                >
                  ü§í SAKIT
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>üìã Riwayat Absensi (7 Hari Terakhir)</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Jenis</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getRecentAbsensi().map((abs) => (
                <tr key={abs.id}>
                  <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                  <td>
                    <span className={`badge ${abs.jenis_absensi === 'masuk' ? 'badge-primary' : 'badge-secondary'}`}>
                      {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                    </span>
                  </td>
                  <td>{abs.status.toUpperCase()}</td>
                  <td>{abs.keterangan || '-'}</td>
                  <td>
                    {abs.foto && <img src={abs.foto} alt="Absensi" width={50} height={50} />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
