import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

const Login = ({ onLogin, currentUser }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isSignUp, setIsSignUp] = useState(false);
  const [signUpData, setSignUpData] = useState({ username: '', password: '', name: '', role: 'user' });
  const [users, setUsers] = useState([]); // dummyUsers lokal jika tidak global

  if (currentUser) {
    return <Navigate to="/" replace />;
  }

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onLogin(formData.username, formData.password)) {
      setError('');
    } else {
      setError('Username atau password salah!');
    }
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (!signUpData.username || !signUpData.password || !signUpData.name) {
      setError('Semua field harus diisi');
      return;
    }
    if (users.some(u => u.username === signUpData.username)) {
      setError('Username sudah digunakan');
      return;
    }
    setUsers([...users, signUpData]);
    setIsSignUp(false);
    setFormData({ username: signUpData.username, password: signUpData.password });
    setError('');
  };

  return (
    <div className="login-container">
      {!isSignUp ? (
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>🔐 Login Sistem Absensi</h2>
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              placeholder="Masukkan username"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Masukkan password"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Login
          </button>
          <button type="button" className="btn btn-secondary" style={{ marginLeft: 10 }} onClick={() => { setIsSignUp(true); setError(''); }}>
            Sign Up
          </button>
        </form>
      ) : (
        <form className="login-form" onSubmit={handleSignUp}>
          <h2>📝 Sign Up User Baru</h2>
          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              value={signUpData.username}
              onChange={e => setSignUpData({ ...signUpData, username: e.target.value })}
              required
              placeholder="Username baru"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={signUpData.password}
              onChange={e => setSignUpData({ ...signUpData, password: e.target.value })}
              required
              placeholder="Password baru"
            />
          </div>
          <div className="form-group">
            <label>Nama:</label>
            <input
              type="text"
              value={signUpData.name}
              onChange={e => setSignUpData({ ...signUpData, name: e.target.value })}
              required
              placeholder="Nama lengkap"
            />
          </div>
          <div className="form-group">
            <label>Role:</label>
            <select
              value={signUpData.role}
              onChange={e => setSignUpData({ ...signUpData, role: e.target.value })}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button type="submit" className="btn btn-success">
            Daftar
          </button>
          <button type="button" className="btn btn-secondary" style={{ marginLeft: 10 }} onClick={() => { setIsSignUp(false); setError(''); }}>
            Kembali ke Login
          </button>
        </form>
      )}
    </div>
  );
};

export default Login;
