import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import ProtectedRoute from "./components/ProtectedRoute";
import DashboardAdmin from "./pages/DashboardAdmin";
import DashboardUser from "./pages/DashboardUser";
import Rekap from "./pages/Rekap";
import Login from "./pages/Login";
import Capture from "./pages/Capture";
import "./App.css";

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const login = async (username, password) => {
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      if (res.ok) {
        setCurrentUser(data.user);
        return true;
      } else {
        alert(data.error);
        return false;
      }
    } catch (err) {
      alert("Server error");
      return false;
    }
  };

  const logout = () => setCurrentUser(null);

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<Login onLogin={login} currentUser={currentUser} />}
        />
        <Route
          path="/"
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route
            index
            element={
              currentUser?.role === "admin" ? (
                <DashboardAdmin currentUser={currentUser} />
              ) : (
                <DashboardUser currentUser={currentUser} />
              )
            }
          />
          <Route path="/rekap" element={<Rekap />} />
          <Route
            path="/capture"
            element={
              <ProtectedRoute currentUser={currentUser}>
                <Layout currentUser={currentUser} onLogout={logout}>
                  <Capture currentUser={currentUser} />
                </Layout>
              </ProtectedRoute>
            }
          />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
