import React, { useState, useRef, useEffect } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const [jenisAbsensi, setJenisAbsensi] = useState('masuk');
  const [loading, setLoading] = useState(false);
  const [localAbsensiData, setLocalAbsensiData] = useState([]);
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // Gunakan useEffect untuk sync data
  useEffect(() => {
    setLocalAbsensiData(absensiData);
  }, [absensiData]);

  // DEBUG: Log data
  console.log("ğŸ“Š ABSENSI DATA:", absensiData);
  console.log("ğŸ“Š LOCAL ABSENSI DATA:", localAbsensiData);
  console.log("ğŸ‘¤ CURRENT USER ID:", currentUser?.id);
  console.log("ğŸ“… TODAY:", today);

  // Cek apakah sudah absen masuk hari ini
  const hasAbsensiMasukToday = localAbsensiData.some(abs => {
    const isMatch = abs.user_id === currentUser?.id && 
                   abs.tanggal === today && 
                   abs.jenis_absensi === 'masuk';
    console.log("ğŸ” Check masuk - User:", abs.user_id, "Tanggal:", abs.tanggal, "Jenis:", abs.jenis_absensi, "Match:", isMatch);
    return isMatch;
  });

  // Cek apakah sudah absen pulang hari ini
  const hasAbsensiPulangToday = localAbsensiData.some(abs => {
    const isMatch = abs.user_id === currentUser?.id && 
                   abs.tanggal === today && 
                   abs.jenis_absensi === 'pulang';
    console.log("ğŸ” Check pulang - User:", abs.user_id, "Tanggal:", abs.tanggal, "Jenis:", abs.jenis_absensi, "Match:", isMatch);
    return isMatch;
  });

  const todayAbsensiMasuk = localAbsensiData.find(
    abs => abs.user_id === currentUser?.id && abs.tanggal === today && abs.jenis_absensi === 'masuk'
  );

  const todayAbsensiPulang = localAbsensiData.find(
    abs => abs.user_id === currentUser?.id && abs.tanggal === today && abs.jenis_absensi === 'pulang'
  );

  console.log("âœ… HAS MASUK:", hasAbsensiMasukToday, "DATA:", todayAbsensiMasuk);
  console.log("âœ… HAS PULANG:", hasAbsensiPulangToday, "DATA:", todayAbsensiPulang);

  // ğŸ“¸ Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false);
  };

  const handleAbsensi = async (status) => {
    if (loading) return;
    
    setLoading(true);
    
    try {
      console.log("ğŸ”„ Mengirim absensi:", { 
        user_id: currentUser.id, 
        status, 
        jenisAbsensi,
        hasFoto: !!foto 
      });

      const absensiPayload = {
        user_id: currentUser.id,
        status,
        keterangan: keterangan || null,
        foto: foto || null,
        jenis_absensi: jenisAbsensi
      };

      console.log("ğŸ“¤ Data yang dikirim:", absensiPayload);

      const response = await fetch("http://localhost:5001/absensi", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json"
        },
        body: JSON.stringify(absensiPayload),
      });

      const data = await response.json();
      
      console.log("ğŸ“¥ Response dari server:", data);

      if (response.ok) {
        alert("âœ… " + data.message);
        // Reset form setelah berhasil
        setKeterangan('');
        setFoto(null);
        
        // Tambahkan data baru ke local state untuk immediate update
        const newAbsensi = {
          id: data.id || Date.now(),
          user_id: currentUser.id,
          status,
          keterangan: keterangan || null,
          foto: foto || null,
          tanggal: today,
          jenis_absensi: jenisAbsensi,
          created_at: new Date().toISOString()
        };
        
        setLocalAbsensiData(prev => [newAbsensi, ...prev]);
        
        // Refresh data dari parent setelah 1 detik
        setTimeout(() => {
          if (onAddAbsensi) {
            onAddAbsensi(newAbsensi);
          }
        }, 1000);
        
      } else {
        alert("âŒ Gagal absen: " + data.error);
      }
    } catch (error) {
      console.error("âŒ Error:", error);
      alert("âŒ Terjadi kesalahan saat mengirim absensi: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getRecentAbsensi = () => {
    const userAbsensi = localAbsensiData.filter(abs => abs.user_id === currentUser?.id);
    console.log("ğŸ“‹ User absensi:", userAbsensi);
    
    return userAbsensi
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  // Fungsi untuk menentukan apakah tombol absensi harus disabled
  const isAbsensiDisabled = () => {
    if (loading) return true;
    if (jenisAbsensi === 'pulang' && !hasAbsensiMasukToday) return true;
    return false;
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>ğŸ‘‹ Selamat Datang, {currentUser?.name}!</h1>
        <p>NIS: {currentUser?.nis} | Kelas: {currentUser?.kelas}</p>
        <p>ğŸ“… {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('âŒ') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>âœï¸ Absensi Hari Ini</h3>
          
          {/* Status Absensi Hari Ini */}
          <div className="today-status" style={{ marginBottom: '20px', padding: '15px', background: '#f8f9fa', borderRadius: '8px' }}>
            <h4>Status Absensi Hari Ini:</h4>
            <div style={{ display: 'flex', gap: '20px', marginTop: '10px' }}>
              <div>
                <strong>Masuk:</strong> 
                {hasAbsensiMasukToday ? (
                  <span className={`status-badge status-${todayAbsensiMasuk?.status}`} style={{ marginLeft: '10px', padding: '4px 8px', borderRadius: '4px', color: 'white', backgroundColor: '#28a745' }}>
                    {todayAbsensiMasuk?.status?.toUpperCase() || 'HADIR'}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
              <div>
                <strong>Pulang:</strong> 
                {hasAbsensiPulangToday ? (
                  <span className={`status-badge status-${todayAbsensiPulang?.status}`} style={{ marginLeft: '10px', padding: '4px 8px', borderRadius: '4px', color: 'white', backgroundColor: '#28a745' }}>
                    {todayAbsensiPulang?.status?.toUpperCase() || 'HADIR'}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
            </div>
          </div>

          {/* Tampilkan form jika belum absen untuk jenis yang dipilih */}
          {!hasAbsensiMasukToday || !hasAbsensiPulangToday ? (
            <div>
              {/* Kolom Masuk dan Pulang */}
              <div className="form-group">
                <label>Jenis Absensi:</label>
                <div className="absensi-type-buttons" style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('masuk')}
                    className={`btn ${jenisAbsensi === 'masuk' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'masuk' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'masuk' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'masuk' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: (loading || hasAbsensiMasukToday) ? 'not-allowed' : 'pointer',
                      opacity: (loading || hasAbsensiMasukToday) ? 0.6 : 1
                    }}
                    disabled={loading || hasAbsensiMasukToday}
                  >
                    ğŸ¢ Masuk {hasAbsensiMasukToday && 'âœ“'}
                  </button>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('pulang')}
                    className={`btn ${jenisAbsensi === 'pulang' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'pulang' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'pulang' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'pulang' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: (loading || !hasAbsensiMasukToday || hasAbsensiPulangToday) ? 'not-allowed' : 'pointer',
                      opacity: (loading || !hasAbsensiMasukToday || hasAbsensiPulangToday) ? 0.6 : 1
                    }}
                    disabled={loading || !hasAbsensiMasukToday || hasAbsensiPulangToday}
                  >
                    ğŸ  Pulang {hasAbsensiPulangToday && 'âœ“'}
                  </button>
                </div>
                <div style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
                  {jenisAbsensi === 'pulang' && !hasAbsensiMasukToday && (
                    <span style={{ color: '#dc3545' }}>
                      âš ï¸ Anda harus absen masuk terlebih dahulu sebelum absen pulang
                    </span>
                  )}
                  {jenisAbsensi === 'pulang' && hasAbsensiPulangToday && (
                    <span style={{ color: '#28a745' }}>
                      âœ… Anda sudah absen pulang hari ini
                    </span>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                  disabled={loading}
                  rows="3"
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    style={{ marginTop: '10px', background: 'blue', color: 'white', padding: '8px 15px', borderRadius: '5px', border: 'none', cursor: 'pointer' }}
                    disabled={loading}
                  >
                    ğŸ“· Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div style={{ marginTop: '10px' }}>
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px', border: 'none', cursor: 'pointer' }}
                      disabled={loading}
                    >
                      ğŸ“¸ Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                    <br />
                    <button 
                      onClick={() => setFoto(null)} 
                      style={{ marginTop: '10px', background: 'gray', color: 'white', padding: '6px 12px', borderRadius: '5px', border: 'none', cursor: 'pointer' }}
                      disabled={loading}
                    >
                      ğŸ”„ Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons" style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                <button 
                  onClick={() => handleAbsensi('hadir')} 
                  className="btn btn-success"
                  disabled={isAbsensiDisabled()}
                  style={{ 
                    flex: 1, 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: 'none', 
                    cursor: isAbsensiDisabled() ? 'not-allowed' : 'pointer',
                    opacity: isAbsensiDisabled() ? 0.6 : 1,
                    backgroundColor: '#28a745',
                    color: 'white'
                  }}
                >
                  {loading ? 'â³ Mengirim...' : 'âœ… HADIR'}
                </button>
                <button 
                  onClick={() => handleAbsensi('izin')} 
                  className="btn btn-warning"
                  disabled={isAbsensiDisabled()}
                  style={{ 
                    flex: 1, 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: 'none', 
                    cursor: isAbsensiDisabled() ? 'not-allowed' : 'pointer',
                    opacity: isAbsensiDisabled() ? 0.6 : 1,
                    backgroundColor: '#ffc107',
                    color: '#212529'
                  }}
                >
                  {loading ? 'â³ Mengirim...' : 'ğŸ“ IZIN'}
                </button>
                <button 
                  onClick={() => handleAbsensi('sakit')} 
                  className="btn btn-danger"
                  disabled={isAbsensiDisabled()}
                  style={{ 
                    flex: 1, 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: 'none', 
                    cursor: isAbsensiDisabled() ? 'not-allowed' : 'pointer',
                    opacity: isAbsensiDisabled() ? 0.6 : 1,
                    backgroundColor: '#dc3545',
                    color: 'white'
                  }}
                >
                  {loading ? 'â³ Mengirim...' : 'ğŸ¤’ SAKIT'}
                </button>
              </div>

              {/* Debug Info */}
              <div style={{ marginTop: '20px', fontSize: '12px', color: '#666', background: '#f5f5f5', padding: '10px', borderRadius: '5px', border: '1px solid #ddd' }}>
                <strong>Debug Info:</strong><br />
                User ID: {currentUser?.id}<br />
                Jenis Absensi: {jenisAbsensi}<br />
                Sudah Absen Masuk: {hasAbsensiMasukToday ? 'Ya' : 'Tidak'}<br />
                Sudah Absen Pulang: {hasAbsensiPulangToday ? 'Ya' : 'Tidak'}<br />
                Tombol Disabled: {isAbsensiDisabled() ? 'Ya' : 'Tidak'}<br />
                Total Data Absensi: {localAbsensiData.length}
              </div>
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>âœ… Absensi Hari Ini Selesai</h2>
              <p>Anda sudah melakukan absensi masuk dan pulang hari ini.</p>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>ğŸ“‹ Riwayat Absensi (7 Hari Terakhir)</h3>
          {getRecentAbsensi().length > 0 ? (
            <table className="table" style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f8f9fa' }}>
                  <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Tanggal</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Jenis</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Status</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Keterangan</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Foto</th>
                </tr>
              </thead>
              <tbody>
                {getRecentAbsensi().map((abs, index) => (
                  <tr key={abs.id || index} style={{ borderBottom: '1px solid #ddd' }}>
                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                      {new Date(abs.tanggal).toLocaleDateString('id-ID')}
                    </td>
                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                      <span style={{ 
                        padding: '4px 8px', 
                        borderRadius: '4px', 
                        fontSize: '12px',
                        backgroundColor: abs.jenis_absensi === 'masuk' ? '#007bff' : '#6c757d',
                        color: 'white'
                      }}>
                        {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                      </span>
                    </td>
                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                      <span style={{ 
                        padding: '4px 8px', 
                        borderRadius: '4px', 
                        fontSize: '12px',
                        backgroundColor: 
                          abs.status === 'hadir' ? '#28a745' : 
                          abs.status === 'izin' ? '#ffc107' : '#dc3545',
                        color: abs.status === 'izin' ? '#212529' : 'white'
                      }}>
                        {abs.status?.toUpperCase() || 'HADIR'}
                      </span>
                    </td>
                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                      {abs.keterangan || '-'}
                    </td>
                    <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                      {abs.foto ? (
                        <img src={abs.foto} alt="Absensi" width={50} height={50} style={{ objectFit: 'cover', borderRadius: '4px' }} />
                      ) : (
                        '-'
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
              Tidak ada riwayat absensi
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
