import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import ProtectedRoute from './components/ProtectedRoute';
import DashboardAdmin from './pages/DashboardAdmin';
import DashboardUser from './pages/DashboardUser';
import Rekap from './pages/Rekap';
import Login from './pages/Login';
import Capture from './pages/Capture';
import { dummyUsers as initialDummyUsers, dummySiswa, dummyAbsensi } from './data/dummyData';
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [dummyUsers, setDummyUsers] = useState(initialDummyUsers);
  const [siswaData, setSiswaData] = useState(dummySiswa);
  const [absensiData, setAbsensiData] = useState(dummyAbsensi);

  const login = (username, password) => {
    const user = dummyUsers.find(u => u.username === username && u.password === password);
    if (user) {
      setCurrentUser(user);
      return true;
    }
    return false;
  };

  const signUp = (newUser) => {
    const exists = dummyUsers.some(
      u => u.username === newUser.username && u.password === newUser.password
    );
    if (exists) return false;
    setDummyUsers(prev => [...prev, newUser]);
    return true;
  };

  const logout = () => {
    setCurrentUser(null);
  };

  const addSiswa = (siswa) => {
    const newSiswa = { id: siswaData.length + 1, ...siswa };
    setSiswaData([...siswaData, newSiswa]);
  };

 const addAbsensi = async (absensi) => {
  try {
    const res = await fetch("http://localhost:5000/absensi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        user_id: currentUser.id,  // HARUS ADA
        status: absensi.status,
        keterangan: absensi.keterangan || "",
        foto: absensi.foto || "",
      }),
    });

    const data = await res.json();
    console.log("üì© Respon server:", data);

    if (!res.ok) {
      alert(data.error || "Gagal mengirim absensi");
      return;
    }

    // update state lokal
    setAbsensiData(prev => [
      ...prev,
      { id: data.id, user_id: currentUser.id, ...absensi, tanggal: new Date().toISOString().split("T")[0] }
    ]);

    alert("‚úÖ Absensi berhasil dikirim!");
  } catch (err) {
    console.error("‚ùå Error:", err);
    alert("Terjadi kesalahan saat mengirim absensi");
  }
};


  return (
    <Router>
      <Routes>
        <Route 
          path="/login" 
          element={<Login onLogin={login} currentUser={currentUser} onSignUp={signUp} dummyUsers={dummyUsers} />} 
        />
        <Route 
          path="/" 
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route 
            index 
            element={
              currentUser?.role === 'admin' ? (
                <DashboardAdmin siswaData={siswaData} absensiData={absensiData} onAddSiswa={addSiswa} />
              ) : (
                <DashboardUser currentUser={currentUser} absensiData={absensiData} onAddAbsensi={addAbsensi} />
              )
            } 
          />
         <Route 
  path="/capture" 
  element={
    <ProtectedRoute currentUser={currentUser}>
      <Layout currentUser={currentUser} onLogout={logout}>
        <Capture currentUser={currentUser} onAddAbsensi={addAbsensi} />
      </Layout>
    </ProtectedRoute>
  }
/>

        </Route>
      </Routes>
    </Router>
  );
}

export default App;
