// src/components/Layout.js
import React from "react";
import { Outlet, Link } from "react-router-dom";

export default function Layout({ currentUser, onLogout }) {
  return (
    <div>
      <header style={{ background: "#4f46e5", color: "#fff", padding: 12 }}>
        <div style={{ maxWidth: 1100, margin: "0 auto", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <div><Link to="/" style={{ color: "#fff", textDecoration: "none", fontWeight: 700 }}>AbsensiApp</Link></div>
          <nav>
            <Link to="/" style={{ color: "#fff", marginRight: 12 }}>Dashboard</Link>
            <Link to="/capture" style={{ color: "#fff", marginRight: 12 }}>Capture</Link>
            <Link to="/rekap" style={{ color: "#fff", marginRight: 12 }}>Rekap</Link>
            <button onClick={onLogout} style={{ background: "transparent", border: "1px solid #fff", color: "#fff", padding: "6px 10px", borderRadius: 6 }}>Logout</button>
          </nav>
        </div>
      </header>

      <main style={{ padding: 20 }}>
        <Outlet />
      </main>
    </div>
  );
}
