import React, { useState } from 'react';

const DashboardAdmin = ({ siswaData, absensiData, onAddSiswa }) => {
  const [formSiswa, setFormSiswa] = useState({
    name: '',
    nis: '',
    kelas: ''
  });
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');
  const [modalFoto, setModalFoto] = useState(null);
  const [activeTab, setActiveTab] = useState('all');

  const handleInputChange = (e) => {
    setFormSiswa({
      ...formSiswa,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await onAddSiswa(formSiswa);
    if (result !== false) {
      setFormSiswa({ name: '', nis: '', kelas: '' });
      setShowForm(false);
      setMessage('âœ… Siswa berhasil ditambahkan!');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const today = new Date().toISOString().split('T')[0];

  // Filter siswa berdasarkan kelas
  const getFilteredSiswa = () => {
    if (!siswaData || siswaData.length === 0) return [];
    
    let filtered = [...siswaData];
    
    if (activeTab === 'xii-rpl1') {
      filtered = siswaData.filter(s => s.kelas === 'XII RPL 1');
    } else if (activeTab === 'xii-rpl2') {
      filtered = siswaData.filter(s => s.kelas === 'XII RPL 2');
    } else if (activeTab === 'xii-rpl3') {
      filtered = siswaData.filter(s => s.kelas === 'XII RPL 3');
    }
    
    return filtered;
  };

  // Filter absensi hari ini saja
  const getAbsensiToday = () => {
    if (!absensiData || absensiData.length === 0) return [];
    
    return absensiData.filter(abs => abs.tanggal === today);
  };

  // Filter absensi berdasarkan kelas
  const getFilteredAbsensiToday = () => {
    const todayAbsensi = getAbsensiToday();
    
    if (activeTab === 'all') return todayAbsensi;
    
    if (activeTab === 'xii-rpl1') {
      return todayAbsensi.filter(abs => abs.kelas === 'XII RPL 1');
    } else if (activeTab === 'xii-rpl2') {
      return todayAbsensi.filter(abs => abs.kelas === 'XII RPL 2');
    } else if (activeTab === 'xii-rpl3') {
      return todayAbsensi.filter(abs => abs.kelas === 'XII RPL 3');
    }
    
    return todayAbsensi;
  };

  // Hitung jumlah status
  const getStatusCount = (status) => {
    return getFilteredAbsensiToday().filter(abs => abs.status === status).length;
  };

  // Cek status absensi siswa hari ini
  const getSiswaAbsenStatus = (siswaId) => {
    const todayAbsensi = getFilteredAbsensiToday();
    
    const absenMasuk = todayAbsensi.find(
      abs => abs.user_id === siswaId && abs.jenis_absensi === 'masuk'
    );
    
    const absenPulang = todayAbsensi.find(
      abs => abs.user_id === siswaId && abs.jenis_absensi === 'pulang'
    );

    if (absenMasuk && absenPulang) {
      return {
        hasAbsen: true,
        status: 'complete',
        label: 'âœ… LENGKAP',
        color: '#28a745',
        detail: `${absenMasuk.status.toUpperCase()} (Masuk & Pulang)`,
        absenMasuk,
        absenPulang
      };
    } else if (absenMasuk) {
      return {
        hasAbsen: true,
        status: 'partial',
        label: 'âš ï¸ MASUK SAJA',
        color: '#ffc107',
        detail: `${absenMasuk.status.toUpperCase()} (Belum absen pulang)`,
        absenMasuk,
        absenPulang: null
      };
    }
    
    return {
      hasAbsen: false,
      status: 'absent',
      label: 'âŒ BELUM ABSEN',
      color: '#dc3545',
      detail: 'Belum mengisi absensi'
    };
  };

  // Pisahkan siswa yang sudah dan belum absen
  const categorizeSiswa = () => {
    const filtered = getFilteredSiswa();
    const sudahAbsen = [];
    const belumAbsen = [];

    filtered.forEach(siswa => {
      const statusAbsen = getSiswaAbsenStatus(siswa.id);
      if (statusAbsen.hasAbsen) {
        sudahAbsen.push({ ...siswa, statusAbsen });
      } else {
        belumAbsen.push(siswa);
      }
    });

    return { sudahAbsen, belumAbsen };
  };

  const { sudahAbsen, belumAbsen } = categorizeSiswa();
  const filteredSiswa = getFilteredSiswa();

  // Get all absensi (not just today) for history
  const getAllFilteredAbsensi = () => {
    if (!absensiData || absensiData.length === 0) return [];
    
    if (activeTab === 'all') return absensiData;
    
    if (activeTab === 'xii-rpl1') {
      return absensiData.filter(abs => abs.kelas === 'XII RPL 1');
    } else if (activeTab === 'xii-rpl2') {
      return absensiData.filter(abs => abs.kelas === 'XII RPL 2');
    } else if (activeTab === 'xii-rpl3') {
      return absensiData.filter(abs => abs.kelas === 'XII RPL 3');
    }
    
    return absensiData;
  };

  const getTabTitle = () => {
    if (activeTab === 'xii-rpl1') return '(XII RPL 1)';
    if (activeTab === 'xii-rpl2') return '(XII RPL 2)';
    if (activeTab === 'xii-rpl3') return '(XII RPL 3)';
    return '(Semua Kelas)';
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>ğŸ‘¨â€ğŸ’¼ Dashboard Admin</h1>
        <p>Kelola data siswa dan pantau absensi harian</p>
        <p style={{ fontSize: '14px', color: '#666' }}>
          ğŸ“… {new Date().toLocaleDateString('id-ID', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          })}
        </p>
      </div>

      {message && (
        <div className="alert alert-success">{message}</div>
      )}

      {/* Tabs */}
      <div style={{ marginBottom: '20px' }}>
        <div style={{ display: 'flex', gap: '10px', borderBottom: '2px solid #e9ecef', flexWrap: 'wrap' }}>
          {[
            { key: 'all', label: 'ğŸ“Š Semua Kelas', color: '#007bff' },
            { key: 'xii-rpl1', label: 'ğŸ‘¥ XII RPL 1', color: '#28a745' },
            { key: 'xii-rpl2', label: 'ğŸ‘¥ XII RPL 2', color: '#ffc107' },
            { key: 'xii-rpl3', label: 'ğŸ‘¥ XII RPL 3', color: '#dc3545' }
          ].map(tab => (
            <button
              key={tab.key}
              onClick={() => setActiveTab(tab.key)}
              style={{
                padding: '12px 20px',
                border: 'none',
                background: activeTab === tab.key ? tab.color : 'transparent',
                color: activeTab === tab.key ? (tab.key === 'xii-rpl2' ? '#212529' : 'white') : '#666',
                borderRadius: '8px 8px 0 0',
                cursor: 'pointer',
                fontWeight: 'bold'
              }}
            >
              {tab.label}
            </button>
          ))}
        </div>
      </div>

      {/* Statistik */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>ğŸ“ˆ Statistik Absensi Hari Ini {getTabTitle()}</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '15px', marginTop: '15px' }}>
          <div style={{ textAlign: 'center', padding: '15px', background: '#28a745', color: 'white', borderRadius: '8px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('hadir')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>âœ… Hadir</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#ffc107', color: '#212529', borderRadius: '8px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('izin')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>ğŸ“ Izin</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#dc3545', color: 'white', borderRadius: '8px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('sakit')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>ğŸ¤’ Sakit</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#6c757d', color: 'white', borderRadius: '8px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{belumAbsen.length}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>âŒ Belum Absen</p>
          </div>
        </div>
      </div>

      {/* Siswa Sudah Absen */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>âœ… Siswa Sudah Absen {getTabTitle()} ({sudahAbsen.length} siswa)</h3>
        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Status Absensi</th>
                <th>Detail</th>
              </tr>
            </thead>
            <tbody>
              {sudahAbsen.length > 0 ? (
                sudahAbsen.map((siswa, idx) => (
                  <tr key={siswa.id}>
                    <td>{idx + 1}</td>
                    <td>{siswa.nis}</td>
                    <td>{siswa.name}</td>
                    <td>{siswa.kelas}</td>
                    <td>
                      <span style={{ 
                        padding: '6px 12px', 
                        borderRadius: '4px', 
                        fontSize: '12px',
                        fontWeight: 'bold',
                        backgroundColor: siswa.statusAbsen.color,
                        color: 'white'
                      }}>
                        {siswa.statusAbsen.label}
                      </span>
                    </td>
                    <td style={{ fontSize: '13px', color: '#666' }}>
                      {siswa.statusAbsen.detail}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center', padding: '20px', color: '#999' }}>
                    Belum ada siswa yang absen hari ini
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Siswa Belum Absen */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>âŒ Siswa Belum Absen {getTabTitle()} ({belumAbsen.length} siswa)</h3>
        
        <button 
          onClick={() => setShowForm(!showForm)}
          style={{ 
            marginBottom: '20px',
            padding: '10px 20px',
            background: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          {showForm ? 'âŒ Batal' : 'â• Tambah Siswa'}
        </button>

        {showForm && (
          <form onSubmit={handleSubmit} style={{ marginBottom: '20px', padding: '20px', background: '#f8f9fa', borderRadius: '10px' }}>
            <div style={{ display: 'grid', gap: '15px' }}>
              <div>
                <label>Nama Lengkap:</label>
                <input
                  type="text"
                  name="name"
                  value={formSiswa.name}
                  onChange={handleInputChange}
                  required
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
              <div>
                <label>NIS:</label>
                <input
                  type="text"
                  name="nis"
                  value={formSiswa.nis}
                  onChange={handleInputChange}
                  required
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              </div>
              <div>
                <label>Kelas:</label>
                <select
                  name="kelas"
                  value={formSiswa.kelas}
                  onChange={handleInputChange}
                  required
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                >
                  <option value="">Pilih Kelas</option>
                  <option value="XII RPL 1">XII RPL 1</option>
                  <option value="XII RPL 2">XII RPL 2</option>
                  <option value="XII RPL 3">XII RPL 3</option>
                </select>
              </div>
            </div>
            <button type="submit" style={{ 
              marginTop: '15px',
              padding: '10px 20px',
              background: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}>
              ğŸ’¾ Simpan
            </button>
          </form>
        )}

        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {belumAbsen.length > 0 ? (
                belumAbsen.map((siswa, idx) => (
                  <tr key={siswa.id}>
                    <td>{idx + 1}</td>
                    <td>{siswa.nis}</td>
                    <td>{siswa.name}</td>
                    <td>{siswa.kelas}</td>
                    <td style={{ color: '#dc3545', fontWeight: 'bold' }}>
                      âŒ BELUM ABSEN
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" style={{ textAlign: 'center', padding: '20px', color: '#28a745', fontWeight: 'bold' }}>
                    âœ… Semua siswa sudah absen!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Riwayat Absensi */}
      <div className="card">
        <h3>ğŸ“‹ Riwayat Absensi Lengkap {getTabTitle()}</h3>
        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>Tanggal</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Jenis</th>
                <th>Status</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getAllFilteredAbsensi().length > 0 ? (
                getAllFilteredAbsensi()
                  .sort((a, b) => new Date(b.created_at || b.tanggal) - new Date(a.created_at || a.tanggal))
                  .map((abs, idx) => (
                    <tr key={abs.id || idx}>
                      <td>{idx + 1}</td>
                      <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                      <td>{abs.nis}</td>
                      <td>{abs.name}</td>
                      <td>{abs.kelas}</td>
                      <td>
                        <span style={{ 
                          padding: '4px 8px', 
                          borderRadius: '4px', 
                          fontSize: '12px',
                          backgroundColor: abs.jenis_absensi === 'masuk' ? '#007bff' : '#6c757d',
                          color: 'white',
                          fontWeight: 'bold'
                        }}>
                          {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                        </span>
                      </td>
                      <td>
                        <span style={{ 
                          padding: '4px 8px', 
                          borderRadius: '4px', 
                          fontSize: '12px',
                          fontWeight: 'bold',
                          backgroundColor: 
                            abs.status === 'hadir' ? '#28a745' : 
                            abs.status === 'izin' ? '#ffc107' : '#dc3545',
                          color: abs.status === 'izin' ? '#212529' : 'white'
                        }}>
                          {abs.status.toUpperCase()}
                        </span>
                      </td>
                      <td>
                        {abs.foto ? (
                          <img 
                            src={abs.foto} 
                            alt="Absensi" 
                            width={40} 
                            height={40} 
                            style={{ cursor: 'pointer', borderRadius: '4px', objectFit: 'cover' }}
                            onClick={() => setModalFoto(abs.foto)}
                          />
                        ) : '-'}
                      </td>
                    </tr>
                  ))
              ) : (
                <tr>
                  <td colSpan="8" style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
                    Tidak ada data absensi
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal Foto */}
      {modalFoto && (
        <div
          style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
            cursor: 'pointer'
          }}
          onClick={() => setModalFoto(null)}
        >
          <img 
            src={modalFoto} 
            alt="Foto Absensi" 
            style={{ 
              maxWidth: '90vw', 
              maxHeight: '90vh', 
              borderRadius: '12px', 
              boxShadow: '0 0 30px rgba(0,0,0,0.5)',
              border: '3px solid white'
            }} 
          />
        </div>
      )}
    </div>
  );
};

export default DashboardAdmin;
