import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const [jenisAbsensi, setJenisAbsensi] = useState('masuk');
  const [loading, setLoading] = useState(false);
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // DEBUG: Log semua data absensi untuk user ini
  console.log("📊 ALL ABSENSI DATA:", absensiData);
  console.log("👤 CURRENT USER:", currentUser);
  console.log("📅 TODAY:", today);

  // Cek apakah sudah absen masuk hari ini - PERBAIKI LOGIKA INI
  const hasAbsensiMasukToday = absensiData.some(abs => {
    const isMatch = abs.user_id === currentUser.id && 
                   abs.tanggal === today && 
                   abs.jenis_absensi === 'masuk';
    console.log("🔍 Check absensi masuk:", { 
      user_id: abs.user_id, 
      currentUser_id: currentUser.id,
      tanggal: abs.tanggal, 
      today: today,
      jenis_absensi: abs.jenis_absensi,
      isMatch: isMatch
    });
    return isMatch;
  });

  // Cek apakah sudah absen pulang hari ini
  const hasAbsensiPulangToday = absensiData.some(abs => {
    const isMatch = abs.user_id === currentUser.id && 
                   abs.tanggal === today && 
                   abs.jenis_absensi === 'pulang';
    console.log("🔍 Check absensi pulang:", { 
      user_id: abs.user_id, 
      currentUser_id: currentUser.id,
      tanggal: abs.tanggal, 
      today: today,
      jenis_absensi: abs.jenis_absensi,
      isMatch: isMatch
    });
    return isMatch;
  });

  const todayAbsensiMasuk = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'masuk'
  );

  const todayAbsensiPulang = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today && abs.jenis_absensi === 'pulang'
  );

  console.log("✅ HAS ABSENSI MASUK TODAY:", hasAbsensiMasukToday);
  console.log("✅ HAS ABSENSI PULANG TODAY:", hasAbsensiPulangToday);
  console.log("📝 TODAY ABSENSI MASUK:", todayAbsensiMasuk);
  console.log("📝 TODAY ABSENSI PULANG:", todayAbsensiPulang);

  // 📸 Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false);
  };

  const handleAbsensi = async (status) => {
    if (loading) return;
    
    setLoading(true);
    
    try {
      console.log("🔄 Mengirim absensi:", { 
        user_id: currentUser.id, 
        status, 
        jenisAbsensi,
        hasFoto: !!foto 
      });

      const absensiData = {
        user_id: currentUser.id,
        status,
        keterangan: keterangan || null,
        foto: foto || null,
        jenis_absensi: jenisAbsensi
      };

      console.log("📤 Data yang dikirim:", absensiData);

      const response = await fetch("http://localhost:5001/absensi", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json"
        },
        body: JSON.stringify(absensiData),
      });

      const data = await response.json();
      
      console.log("📥 Response dari server:", data);

      if (response.ok) {
        alert("✅ " + data.message);
        // Reset form setelah berhasil
        setKeterangan('');
        setFoto(null);
        // Refresh halaman untuk update data terbaru
        setTimeout(() => {
          window.location.reload();
        }, 1000);
      } else {
        alert("❌ Gagal absen: " + data.error);
      }
    } catch (error) {
      console.error("❌ Error:", error);
      alert("❌ Terjadi kesalahan saat mengirim absensi: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.user_id === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  // Fungsi untuk menentukan apakah tombol absensi harus disabled
  const isAbsensiDisabled = () => {
    if (loading) return true;
    if (jenisAbsensi === 'pulang' && !hasAbsensiMasukToday) return true;
    return false;
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>✏️ Absensi Hari Ini</h3>
          
          {/* Status Absensi Hari Ini */}
          <div className="today-status" style={{ marginBottom: '20px', padding: '15px', background: '#f8f9fa', borderRadius: '8px' }}>
            <h4>Status Absensi Hari Ini:</h4>
            <div style={{ display: 'flex', gap: '20px', marginTop: '10px' }}>
              <div>
                <strong>Masuk:</strong> 
                {hasAbsensiMasukToday ? (
                  <span className={`status-badge status-${todayAbsensiMasuk?.status}`} style={{ marginLeft: '10px' }}>
                    {todayAbsensiMasuk?.status?.toUpperCase()}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
              <div>
                <strong>Pulang:</strong> 
                {hasAbsensiPulangToday ? (
                  <span className={`status-badge status-${todayAbsensiPulang?.status}`} style={{ marginLeft: '10px' }}>
                    {todayAbsensiPulang?.status?.toUpperCase()}
                  </span>
                ) : (
                  <span style={{ marginLeft: '10px', color: '#dc3545' }}>Belum Absen</span>
                )}
              </div>
            </div>
          </div>

          {/* Tampilkan form jika belum absen untuk jenis yang dipilih */}
          {(jenisAbsensi === 'masuk' && !hasAbsensiMasukToday) || 
           (jenisAbsensi === 'pulang' && !hasAbsensiPulangToday) ? (
            <div>
              {/* Kolom Masuk dan Pulang */}
              <div className="form-group">
                <label>Jenis Absensi:</label>
                <div className="absensi-type-buttons" style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('masuk')}
                    className={`btn ${jenisAbsensi === 'masuk' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'masuk' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'masuk' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'masuk' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: loading ? 'not-allowed' : 'pointer',
                      opacity: loading ? 0.6 : 1
                    }}
                    disabled={loading}
                  >
                    🏢 Masuk {hasAbsensiMasukToday && '✓'}
                  </button>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('pulang')}
                    className={`btn ${jenisAbsensi === 'pulang' ? 'btn-primary' : 'btn-outline'}`}
                    style={{ 
                      flex: 1, 
                      padding: '10px',
                      border: jenisAbsensi === 'pulang' ? '2px solid #007bff' : '1px solid #ddd',
                      backgroundColor: jenisAbsensi === 'pulang' ? '#007bff' : 'white',
                      color: jenisAbsensi === 'pulang' ? 'white' : '#333',
                      borderRadius: '5px',
                      cursor: (loading || !hasAbsensiMasukToday) ? 'not-allowed' : 'pointer',
                      opacity: (loading || !hasAbsensiMasukToday) ? 0.6 : 1
                    }}
                    disabled={loading || !hasAbsensiMasukToday}
                  >
                    🏠 Pulang {hasAbsensiPulangToday && '✓'}
                  </button>
                </div>
                <div style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
                  {jenisAbsensi === 'pulang' && !hasAbsensiMasukToday && (
                    <span style={{ color: '#dc3545' }}>
                      ⚠️ Anda harus absen masuk terlebih dahulu sebelum absen pulang
                    </span>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                  disabled={loading}
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    style={{ marginTop: '10px', background: 'blue', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                    disabled={loading}
                  >
                    📷 Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div style={{ marginTop: '10px' }}>
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                      disabled={loading}
                    >
                      📸 Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                    <br />
                    <button 
                      onClick={() => setFoto(null)} 
                      style={{ marginTop: '10px', background: 'gray', color: 'white', padding: '6px 12px', borderRadius: '5px' }}
                      disabled={loading}
                    >
                      🔄 Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons">
                <button 
                  onClick={() => handleAbsensi('hadir')} 
                  className="btn btn-success"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '⏳ Mengirim...' : '✅ HADIR'}
                </button>
                <button 
                  onClick={() => handleAbsensi('izin')} 
                  className="btn btn-warning"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '⏳ Mengirim...' : '📝 IZIN'}
                </button>
                <button 
                  onClick={() => handleAbsensi('sakit')} 
                  className="btn btn-danger"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '⏳ Mengirim...' : '🤒 SAKIT'}
                </button>
              </div>

              {/* Debug Info */}
              <div style={{ marginTop: '10px', fontSize: '12px', color: '#666', background: '#f5f5f5', padding: '10px', borderRadius: '5px' }}>
                <strong>Debug Info:</strong><br />
                User ID: {currentUser.id}<br />
                Jenis Absensi: {jenisAbsensi}<br />
                Sudah Absen Masuk: {hasAbsensiMasukToday ? 'Ya' : 'Tidak'}<br />
                Sudah Absen Pulang: {hasAbsensiPulangToday ? 'Ya' : 'Tidak'}<br />
                Tombol Disabled: {isAbsensiDisabled() ? 'Ya' : 'Tidak'}<br />
                Total Data Absensi: {absensiData.length}
              </div>
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>✅ Absensi {jenisAbsensi.toUpperCase()} Sudah Tercatat</h2>
              <p>Anda sudah melakukan absensi {jenisAbsensi} hari ini.</p>
              <button 
                onClick={() => {
                  // Switch ke jenis absensi lainnya
                  if (jenisAbsensi === 'masuk' && !hasAbsensiPulangToday) {
                    setJenisAbsensi('pulang');
                  } else if (jenisAbsensi === 'pulang' && !hasAbsensiMasukToday) {
                    setJenisAbsensi('masuk');
                  }
                }}
                className="btn btn-primary"
                style={{ marginTop: '10px' }}
                disabled={loading}
              >
                {jenisAbsensi === 'masuk' && !hasAbsensiPulangToday 
                  ? '🏠 Absen Pulang' 
                  : jenisAbsensi === 'pulang' && !hasAbsensiMasukToday
                  ? '🏢 Absen Masuk'
                  : 'Lihat Riwayat'
                }
              </button>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>📋 Riwayat Absensi (7 Hari Terakhir)</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Jenis</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getRecentAbsensi().map((abs) => (
                <tr key={abs.id}>
                  <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                  <td>
                    <span className={`badge ${abs.jenis_absensi === 'masuk' ? 'badge-primary' : 'badge-secondary'}`}>
                      {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                    </span>
                  </td>
                  <td>
                    <span className={`status-badge status-${abs.status}`}>
                      {abs.status.toUpperCase()}
                    </span>
                  </td>
                  <td>{abs.keterangan || '-'}</td>
                  <td>
                    {abs.foto && <img src={abs.foto} alt="Absensi" width={50} height={50} />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
