// src/pages/DashboardUser.js
import React, { useEffect, useState } from "react";

export default function DashboardUser({ currentUser, absensiData }) {
  const [list, setList] = useState(absensiData || []);

  useEffect(() => {
    setList(absensiData || []);
  }, [absensiData]);

  return (
    <div style={{ maxWidth: 1000, margin: "20px auto" }}>
      <div className="card">
        <h1>Dashboard {currentUser?.name}</h1>
        <p>Riwayat absensi kamu:</p>
        {list.length === 0 ? (
          <p>Belum ada absensi.</p>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
                <th>Lokasi</th>
              </tr>
            </thead>
            <tbody>
              {list.map((a) => (
                <tr key={a.id || a.tanggal + a.status}>
                  <td>{a.tanggal || "-"}</td>
                  <td>{a.status}</td>
                  <td>{a.keterangan}</td>
                  <td>{a.foto ? <img src={a.foto} alt="abs" style={{ width: 80 }} /> : "-"}</td>
                  <td>{a.latitude && a.longitude ? `${a.latitude}, ${a.longitude}` : "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
