import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Layout from './components/Layout';
import { DummySiswa, DummyAbsensi, dummyUsers } from './data/DummyData';
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [siswaData, setSiswaData] = useState(DummySiswa);
  const [AbsensiData, setAbsensiData] = useState(DummyAbsensi);

  const login = (username, password) => {
    const user = dummyUsers.find(u => u.username === username && u.password === password);
    if (user) {
      setCurrentUser(user);
      return true;
    }
    return false;
  };
  const logout = () => {
    setCurrentUser(null);
  };

  const addSiswa = (siswa) => {
    const newSiswa = {
      id: siswaData.length + 1,
      ...siswa
    };
    setSiswaData([...siswaData, newSiswa]);
  };

  const addAbsensi = (absensi) => {
    const newAbsensi = {
      id: AbsensiData.length + 1,
      ...absensi,
      tanggal: new Date().toISOString().split('T')[0] // Menambahkan tanggal saat ini
    };
    setAbsensiData([...AbsensiData, newAbsensi]);
  };


    }




