import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const hasAbsensiToday = absensiData.some(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const todayAbsensi = absensiData.find(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  // 📸 Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false); // setelah ambil foto, kamera ditutup
  };

const handleAbsensi = async (status) => {
  const response = await fetch("http://localhost:5000/absensi", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      user_id: currentUser.id,
      status,
      keterangan,
      foto
    }),
  });

  const data = await response.json();
  if (response.ok) {
    alert("✅ " + data.message);
  } else {
    alert("❌ Gagal absen: " + data.error);
  }
};

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.siswaId === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('❌') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

     
         
      </div>
  );
};

export default DashboardUser;
