import React, { useState } from 'react';

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [message, setMessage] = useState('');

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const hasAbsensiToday = absensiData.some(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const todayAbsensi = absensiData.find(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFoto(URL.createObjectURL(file));
    }
  };

  const handleAbsensi = (status) => {
    if (hasAbsensiToday) {
      setMessage('❌ Anda sudah melakukan absensi hari ini!');
      return;
    }
    if (!foto) {
      setMessage('❌ Silakan upload foto terlebih dahulu!');
      return;
    }

    const absensiDataObj = {
      siswaId: currentUser.id,
      status: status,
      keterangan: keterangan,
      foto: foto
    };

    onAddAbsensi(absensiDataObj);
    setMessage(`✅ Absensi berhasil! Status: ${status.toUpperCase()}`);
    setKeterangan('');
    setFoto(null);
    setTimeout(() => setMessage(''), 5000);
  };

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.siswaId === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('❌') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>✏️ Absensi Hari Ini</h3>
          
          {hasAbsensiToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <div style={{ 
                fontSize: '48px', 
                marginBottom: '20px',
                color: todayAbsensi?.status === 'hadir' ? '#28a745' : 
                       todayAbsensi?.status === 'izin' ? '#ffc107' : '#dc3545'
              }}>
                {todayAbsensi?.status === 'hadir' ? '✅' : 
                 todayAbsensi?.status === 'izin' ? '📝' : '🤒'}
              </div>
              <h2>Absensi Sudah Tercatat</h2>
              <div className={`status-badge status-${todayAbsensi?.status}`}>
                {todayAbsensi?.status?.toUpperCase()}
              </div>
              {todayAbsensi?.keterangan && (
                <p style={{ marginTop: '10px', fontStyle: 'italic' }}>
                  Keterangan: {todayAbsensi.keterangan}
                </p>
              )}
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                  style={{
                    width: '100%',
                    minHeight: '80px',
                    padding: '12px',
                    border: '2px solid #ddd',
                    borderRadius: '5px',
                    resize: 'vertical'
                  }}
                />
              </div>
              <div className="form-group">
                <label>Upload Foto Absensi:</label>
                <input
                  type="file"
                  accept="image/*"
                  capture="user"
                  onChange={handleFileChange}
                  required
                />
                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={80} height={80} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                  </div>
                )}
              </div>
              <div className="absensi-buttons">
                <button 
                  onClick={() => handleAbsensi('hadir')}
                  className="btn btn-success"
                >
                  ✅ HADIR
                </button>
                <button 
                  onClick={() => handleAbsensi('izin')}
                  className="btn btn-warning"
                >
                  📝 IZIN
                </button>
                <button 
                  onClick={() => handleAbsensi('sakit')}
                  className="btn btn-danger"
                >
                  🤒 SAKIT
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>📋 Riwayat Absensi (7 Hari Terakhir)</h3>
          
          {getRecentAbsensi().length === 0 ? (
            <p style={{ textAlign: 'center', color: '#666', fontStyle: 'italic' }}>
              Belum ada riwayat absensi
            </p>
          ) : (
            <div className="table-container">
              <table className="table">
                <thead>
                  <tr>
                    <th>Tanggal</th>
                    <th>Status</th>
                    <th>Keterangan</th>
                    <th>Foto</th>
                  </tr>
                </thead>
                <tbody>
                  {getRecentAbsensi().map((abs) => (
                    <tr key={abs.id}>
                      <td>
                        {new Date(abs.tanggal).toLocaleDateString('id-ID')}
                      </td>
                      <td>
                        <span className={`status-badge status-${abs.status}`}>
                          {abs.status.toUpperCase()}
                        </span>
                      </td>
                      <td>{abs.keterangan || '-'}</td>
                      <td>
                        {abs.foto && (
                          <img src={abs.foto} alt="Absensi" width={40} height={40} style={{ objectFit: 'cover', borderRadius: '6px' }} />
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
