import React, { useState, useEffect } from "react";

export default function Capture({ currentUser, onAddAbsensi }) {
  const [status, setStatus] = useState("");
  const [keterangan, setKeterangan] = useState("");
  const [foto, setFoto] = useState("");
  const [userLocation, setUserLocation] = useState(null); // ganti nama
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!navigator.geolocation) return setError("Geolocation tidak didukung browser Anda");

    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ latitude: pos.coords.latitude, longitude: pos.coords.longitude }),
      () => setError("Harap aktifkan lokasi untuk capture absensi")
    );
  }, []);

  const handleFotoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onloadend = () => setFoto(reader.result);
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!status) return alert("Status wajib diisi");
    if (!userLocation) return alert("Lokasi belum tersedia");

    await onAddAbsensi({
      user_id: currentUser.id,
      status,
      keterangan: keterangan || "-",
      foto,
      latitude: userLocation.latitude,
      longitude: userLocation.longitude
    });

    setStatus("");
    setKeterangan("");
    setFoto("");
  };

  if (error) return <div className="card">{error}</div>;
  if (!userLocation) return <div className="card">Meminta izin lokasi...</div>;

  return (
    <div className="card">
      <h2>Absen {currentUser?.name}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Status:</label>
          <select value={status} onChange={e => setStatus(e.target.value)} required>
            <option value="">Pilih status</option>
            <option value="hadir">Hadir</option>
            <option value="izin">Izin</option>
            <option value="sakit">Sakit</option>
          </select>
        </div>

        <div className="form-group">
          <label>Keterangan:</label>
          <input type="text" value={keterangan} onChange={e => setKeterangan(e.target.value)} placeholder="Keterangan (opsional)" />
        </div>

        <div className="form-group">
          <label>Foto (opsional):</label>
          <input type="file" accept="image/*" onChange={handleFotoChange} />
        </div>

        <button type="submit">Kirim Absensi</button>
      </form>

      {foto && <div style={{ marginTop: "10px" }}><img src={foto} alt="Preview" style={{ width: "100%", borderRadius: "6px" }} /></div>}

      <div style={{ marginTop: "10px" }}>
        <p>Lokasi:</p>
        <p>Latitude: {userLocation.latitude}</p>
        <p>Longitude: {userLocation.longitude}</p>
      </div>
    </div>
  );
}
