import React, { useState } from 'react';

export default function Capture({ currentUser, onAddAbsensi }) {
  const [status, setStatus] = useState('');
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState('');

  // convert file ke base64 (opsional)
  const handleFotoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => setFoto(reader.result);
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!status) return alert('Status wajib diisi');

    // Kirim ke backend
    await onAddAbsensi({ status, keterangan, foto });

    // Reset form
    setStatus('');
    setKeterangan('');
    setFoto('');
  };

  return (
    <div style={{ maxWidth: '400px', margin: '20px auto', padding: '10px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Absen {currentUser?.name || 'User'}</h2>
      <form onSubmit={handleSubmit}>
        <label>Status:</label>
        <select value={status} onChange={(e) => setStatus(e.target.value)} required>
          <option value="">Pilih status</option>
          <option value="hadir">Hadir</option>
          <option value="izin">Izin</option>
          <option value="sakit">Sakit</option>
        </select>

        <label>Keterangan:</label>
        <input
          type="text"
          value={keterangan}
          onChange={(e) => setKeterangan(e.target.value)}
          placeholder="Keterangan (opsional)"
        />

        <label>Foto (opsional):</label>
        <input type="file" accept="image/*" onChange={handleFotoChange} />

        <button type="submit" style={{ marginTop: '10px' }}>Kirim Absensi</button>
      </form>

      {foto && (
        <div style={{ marginTop: '10px' }}>
          <p>Preview Foto:</p>
          <img src={foto} alt="Preview" style={{ width: '100%' }} />
        </div>
      )}
    </div>
  );
}
