import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ currentUser, onLogout }) => {
  const [open, setOpen] = useState(false);

  return (
    <nav className="navbar bg-blue-600 text-white flex items-center p-4">
      <button 
        className="mr-4 text-2xl md:hidden" 
        onClick={() => setOpen(!open)}
      >
        â˜°
      </button>
      <div className="flex-1 font-bold">ðŸ“š Sistem Absensi</div>
      <div className={`${open ? 'block' : 'hidden'} md:flex space-x-4`}>
        <Link to="/">Dashboard</Link>
        <Link to="/rekap">Rekap</Link>
        <span>Hai, {currentUser?.name}</span>
        <button onClick={onLogout} className="bg-red-500 px-3 py-1 rounded">Logout</button>
      </div>
    </nav>
  );
};

export default Navbar;
