import React, { useState, useRef, useEffect } from 'react';
import Webcam from "react-webcam";
import './DashboardUser.css'; // File CSS terpisah

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const [jenisAbsensi, setJenisAbsensi] = useState('masuk');
  const [loading, setLoading] = useState(false);
  const [localAbsensiData, setLocalAbsensiData] = useState([]);
  const [isNavOpen, setIsNavOpen] = useState(false);
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // Gunakan useEffect untuk sync data
  useEffect(() => {
    setLocalAbsensiData(absensiData);
  }, [absensiData]);

  // Cek apakah sudah absen masuk hari ini
  const hasAbsensiMasukToday = localAbsensiData.some(abs => 
    abs.user_id === currentUser?.id && 
    abs.tanggal === today && 
    abs.jenis_absensi === 'masuk'
  );

  const hasAbsensiPulangToday = localAbsensiData.some(abs => 
    abs.user_id === currentUser?.id && 
    abs.tanggal === today && 
    abs.jenis_absensi === 'pulang'
  );

  // üì∏ Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false);
  };

  const handleAbsensi = async (status) => {
    if (loading) return;
    
    setLoading(true);
    
    try {
      const absensiPayload = {
        user_id: currentUser.id,
        status,
        keterangan: keterangan || null,
        foto: foto || null,
        jenis_absensi: jenisAbsensi
      };

      const response = await fetch("http://localhost:5001/absensi", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json"
        },
        body: JSON.stringify(absensiPayload),
      });

      const data = await response.json();
      
      if (response.ok) {
        alert("‚úÖ " + data.message);
        setKeterangan('');
        setFoto(null);
        
        const newAbsensi = {
          id: data.id || Date.now(),
          user_id: currentUser.id,
          status,
          keterangan: keterangan || null,
          foto: foto || null,
          tanggal: today,
          jenis_absensi: jenisAbsensi,
          created_at: new Date().toISOString()
        };
        
        setLocalAbsensiData(prev => [newAbsensi, ...prev]);
        
        setTimeout(() => {
          if (onAddAbsensi) {
            onAddAbsensi(newAbsensi);
          }
        }, 1000);
        
      } else {
        alert("‚ùå Gagal absen: " + data.error);
      }
    } catch (error) {
      console.error("‚ùå Error:", error);
      alert("‚ùå Terjadi kesalahan saat mengirim absensi: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getRecentAbsensi = () => {
    const userAbsensi = localAbsensiData.filter(abs => abs.user_id === currentUser?.id);
    return userAbsensi
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  // Fungsi untuk menentukan apakah tombol absensi harus disabled
  const isAbsensiDisabled = () => {
    if (loading) return true;
    if (jenisAbsensi === 'pulang' && !hasAbsensiMasukToday) return true;
    return false;
  };

  // Toggle hamburger menu
  const toggleNav = () => {
    setIsNavOpen(!isNavOpen);
  };

  return (
    <div className="dashboard">
      {/* Header dengan Hamburger Menu */}
      <header className="dashboard-header">
        <div className="header-content">
          <button 
            className="hamburger-btn"
            onClick={toggleNav}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
          
          <div className="header-info">
            <h1>üëã Selamat Datang, {currentUser?.name}!</h1>
            <p>NIS: {currentUser?.nis} | Kelas: {currentUser?.kelas}</p>
            <p>üìÖ {todayFormatted}</p>
          </div>
        </div>
      </header>

      {/* Navigation Menu */}
      <nav className={`nav-menu ${isNavOpen ? 'nav-open' : ''}`}>
        <div className="nav-header">
          <h3>Menu</h3>
          <button className="close-nav" onClick={toggleNav}>‚úï</button>
        </div>
        <ul className="nav-links">
          <li><a href="#absensi" onClick={toggleNav}>üìä Absensi</a></li>
          <li><a href="#riwayat" onClick={toggleNav}>üìã Riwayat</a></li>
          <li><a href="#profil" onClick={toggleNav}>üë§ Profil</a></li>
          <li><a href="#pengaturan" onClick={toggleNav}>‚öôÔ∏è Pengaturan</a></li>
          <li><a href="#bantuan" onClick={toggleNav}>‚ùì Bantuan</a></li>
          <li><a href="#keluar" onClick={toggleNav}>üö™ Keluar</a></li>
        </ul>
      </nav>

      {/* Overlay untuk menutup menu saat klik di luar */}
      {isNavOpen && <div className="nav-overlay" onClick={toggleNav}></div>}

      {message && (
        <div className={`alert ${message.includes('‚ùå') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>‚úèÔ∏è Absensi Hari Ini</h3>
          
          {/* Tampilkan form jika belum absen untuk jenis yang dipilih */}
          {!hasAbsensiMasukToday || !hasAbsensiPulangToday ? (
            <div className="absensi-form">
              {/* Kolom Masuk dan Pulang */}
              <div className="form-group">
                <label>Jenis Absensi:</label>
                <div className="absensi-type-buttons">
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('masuk')}
                    className={`absensi-type-btn ${jenisAbsensi === 'masuk' ? 'active' : ''}`}
                    disabled={loading || hasAbsensiMasukToday}
                  >
                    üè¢ Masuk {hasAbsensiMasukToday && '‚úì'}
                  </button>
                  <button 
                    type="button"
                    onClick={() => setJenisAbsensi('pulang')}
                    className={`absensi-type-btn ${jenisAbsensi === 'pulang' ? 'active' : ''}`}
                    disabled={loading || !hasAbsensiMasukToday || hasAbsensiPulangToday}
                  >
                    üè† Pulang {hasAbsensiPulangToday && '‚úì'}
                  </button>
                </div>
                <div className="absensi-info">
                  {jenisAbsensi === 'pulang' && !hasAbsensiMasukToday && (
                    <span className="warning-text">
                      ‚ö†Ô∏è Anda harus absen masuk terlebih dahulu sebelum absen pulang
                    </span>
                  )}
                  {jenisAbsensi === 'pulang' && hasAbsensiPulangToday && (
                    <span className="success-text">
                      ‚úÖ Anda sudah absen pulang hari ini
                    </span>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                  disabled={loading}
                  rows="3"
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    className="camera-btn"
                    disabled={loading}
                  >
                    üì∑ Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div className="camera-container">
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width="100%"
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      className="capture-btn"
                      disabled={loading}
                    >
                      üì∏ Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div className="photo-preview">
                    <img src={foto} alt="Preview" />
                    <button 
                      onClick={() => setFoto(null)} 
                      className="retake-btn"
                      disabled={loading}
                    >
                      üîÑ Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons">
                <button 
                  onClick={() => handleAbsensi('hadir')} 
                  className="absensi-btn hadir-btn"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '‚è≥ Mengirim...' : '‚úÖ HADIR'}
                </button>
                <button 
                  onClick={() => handleAbsensi('izin')} 
                  className="absensi-btn izin-btn"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '‚è≥ Mengirim...' : 'üìù IZIN'}
                </button>
                <button 
                  onClick={() => handleAbsensi('sakit')} 
                  className="absensi-btn sakit-btn"
                  disabled={isAbsensiDisabled()}
                >
                  {loading ? '‚è≥ Mengirim...' : 'ü§í SAKIT'}
                </button>
              </div>
            </div>
          ) : (
            <div className="absensi-complete">
              <h2>‚úÖ Absensi Hari Ini Selesai</h2>
              <p>Anda sudah melakukan absensi masuk dan pulang hari ini.</p>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>üìã Riwayat Absensi (7 Hari Terakhir)</h3>
          <div className="table-container">
            {getRecentAbsensi().length > 0 ? (
              <table className="absensi-table">
                <thead>
                  <tr>
                    <th>Tanggal</th>
                    <th>Jenis</th>
                    <th>Status</th>
                    <th>Keterangan</th>
                    <th>Foto</th>
                  </tr>
                </thead>
                <tbody>
                  {getRecentAbsensi().map((abs, index) => (
                    <tr key={abs.id || index}>
                      <td>
                        {new Date(abs.tanggal).toLocaleDateString('id-ID')}
                      </td>
                      <td>
                        <span className={`badge ${abs.jenis_absensi === 'masuk' ? 'badge-masuk' : 'badge-pulang'}`}>
                          {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                        </span>
                      </td>
                      <td>
                        <span className={`badge ${
                          abs.status === 'hadir' ? 'badge-hadir' : 
                          abs.status === 'izin' ? 'badge-izin' : 'badge-sakit'
                        }`}>
                          {abs.status?.toUpperCase() || 'HADIR'}
                        </span>
                      </td>
                      <td>{abs.keterangan || '-'}</td>
                      <td>
                        {abs.foto ? (
                          <img src={abs.foto} alt="Absensi" className="absensi-photo" />
                        ) : (
                          '-'
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p className="no-data">
                Tidak ada riwayat absensi
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
