import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [absenType, setAbsenType] = useState(null); // 'hadir' | 'izin' | 'sakit' | 'pulang'
  const [showCamera, setShowCamera] = useState(false);
  const [foto, setFoto] = useState(null);
  const webcamRef = useRef(null);
  const [loading, setLoading] = useState(false);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

 const hasAbsensiToday = absensiData.some(
  abs => abs.user_id === currentUser.id && abs.tanggal === today
);

const todayAbsensi = absensiData.find(
  abs => abs.user_id === currentUser.id && abs.tanggal === today
);

  // Handler klik tombol absensi
  const handleClickAbsen = (type) => {
    setAbsenType(type);
    setShowCamera(true);
    setFoto(null);
  };

  // Handler ambil foto dan submit
  const handleCaptureAndSubmit = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false);
    setLoading(true);
    let url = "http://localhost:5001/absensi";
    let payload = {
      user_id: currentUser.id,
      keterangan,
      foto: imageSrc
    };
    if (absenType === 'pulang') {
      url = "http://localhost:5001/absensi/pulang";
      payload.status = 'pulang';
    } else {
      payload.status = absenType;
    }
    try {
      const response = await fetch(url, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });
      const data = await response.json();
      if (response.ok) {
        alert("‚úÖ " + data.message);
      } else {
        alert("‚ùå Gagal absen: " + data.error);
      }
    } catch (error) {
      alert("‚ùå Error: " + error.message);
    }
    setLoading(false);
    setAbsenType(null);
    setFoto(null);
  };

 const getRecentAbsensi = () => {
  return absensiData
    .filter(abs => abs.user_id === currentUser.id) // user_id bukan siswaId
    .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
    .slice(0, 7);
};

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üëã Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>üìÖ {todayFormatted}</p>
      </div>

      {/* Message alert (if needed, but not in new_code) */}
      {/* <div className={`alert ${message.includes('‚ùå') ? 'alert-error' : 'alert-success'}`}>
        {message}
      </div> */}

      <div className="dashboard-content">
        <div className="card">
          <h3>‚úèÔ∏è Absensi Hari Ini</h3>
          {hasAbsensiToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Absensi Sudah Tercatat</h2>
              <div className={`status-badge status-${todayAbsensi?.status}`}>{todayAbsensi?.status?.toUpperCase()}</div>
              {todayAbsensi.pulang_status && (
                <div style={{ marginTop: 16 }}>
                  <div className={`status-badge status-${todayAbsensi.pulang_status}`}>{todayAbsensi.pulang_status?.toUpperCase()}</div>
                  {todayAbsensi.pulang_foto && <img src={todayAbsensi.pulang_foto} alt="Foto Pulang" width={80} style={{ marginTop: 10, borderRadius: 8 }} />}
                </div>
              )}
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>
              {/* Tombol Absensi Bersebelahan */}
              <div className="absensi-buttons" style={{ display: 'flex', gap: '10px', marginTop: '16px', marginBottom: '16px' }}>
                <button onClick={() => handleClickAbsen('hadir')} className="btn btn-success">‚úÖ HADIR</button>
                <button onClick={() => handleClickAbsen('izin')} className="btn btn-warning">üìù IZIN</button>
                <button onClick={() => handleClickAbsen('sakit')} className="btn btn-danger">ü§í SAKIT</button>
                <button onClick={() => handleClickAbsen('pulang')} className="btn btn-primary" disabled={!hasAbsensiToday}>üè† PULANG</button>
              </div>
              {/* Kamera hanya satu, muncul setelah klik tombol */}
              {showCamera && (
                <div style={{ marginTop: '10px', textAlign: 'center' }}>
                  <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    width={250}
                    videoConstraints={{ facingMode: "user" }}
                  />
                  <br />
                  <button 
                    onClick={handleCaptureAndSubmit} 
                    style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                    disabled={loading}
                  >
                    üì∏ Ambil Foto & Submit
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
        

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>üìã Riwayat Absensi (7 Hari Terakhir)</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getRecentAbsensi().map((abs) => (
                <tr key={abs.id}>
                  <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                  <td>{abs.status.toUpperCase()}</td>
                  <td>{abs.keterangan || '-'}</td>
                  <td>
                    {abs.foto && <img src={abs.foto} alt="Absensi" width={50} height={50} />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};



export default DashboardUser;
