const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keteranganMasuk, setKeteranganMasuk] = useState('');
  const [fotoMasuk, setFotoMasuk] = useState(null);
  const [showCameraMasuk, setShowCameraMasuk] = useState(false);
  const [loadingMasuk, setLoadingMasuk] = useState(false);
  const [messageMasuk, setMessageMasuk] = useState('');
  const webcamRefMasuk = useRef(null);

  const [keteranganPulang, setKeteranganPulang] = useState('');
  const [fotoPulang, setFotoPulang] = useState(null);
  const [showCameraPulang, setShowCameraPulang] = useState(false);
  const [loadingPulang, setLoadingPulang] = useState(false);
  const [messagePulang, setMessagePulang] = useState('');
  const webcamRefPulang = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const todayAbsensi = absensiData.find(
    abs => abs.user_id === currentUser.id && abs.tanggal === today
  );

  // Kamera & Foto Masuk
  const capturePhotoMasuk = () => {
    const imageSrc = webcamRefMasuk.current.getScreenshot();
    setFotoMasuk(imageSrc);
    setShowCameraMasuk(false);
  };

  // Kamera & Foto Pulang
  const capturePhotoPulang = () => {
    const imageSrc = webcamRefPulang.current.getScreenshot();
    setFotoPulang(imageSrc);
    setShowCameraPulang(false);
  };

  // Submit Absen Masuk
  const handleAbsensiMasuk = async () => {
    setLoadingMasuk(true);
    setMessageMasuk("");
    try {
      const response = await fetch("http://localhost:5001/absensi", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: currentUser.id,
          status: 'hadir',
          keterangan: keteranganMasuk,
          foto: fotoMasuk
        }),
      });
      const data = await response.json();
      if (response.ok) {
        setMessageMasuk("✅ " + data.message);
        setFotoMasuk(null);
        setKeteranganMasuk("");
      } else {
        setMessageMasuk("❌ Gagal absen masuk: " + data.error);
      }
    } catch (error) {
      setMessageMasuk("❌ Error: " + error.message);
    }
    setLoadingMasuk(false);
  };

  // Submit Absen Pulang
  const handleAbsensiPulang = async () => {
    setLoadingPulang(true);
    setMessagePulang("");
    try {
      const response = await fetch("http://localhost:5001/absensi/pulang", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: currentUser.id,
          status: 'pulang',
          keterangan: keteranganPulang,
          foto: fotoPulang
        }),
      });
      const data = await response.json();
      if (response.ok) {
        setMessagePulang("✅ " + data.message);
        setFotoPulang(null);
        setKeteranganPulang("");
      } else {
        setMessagePulang("❌ Gagal absen pulang: " + data.error);
      }
    } catch (error) {
      setMessagePulang("❌ Error: " + error.message);
    }
    setLoadingPulang(false);
  };

  // Riwayat Absensi
  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.user_id === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      <div className="dashboard-content" style={{ display: 'flex', gap: '24px', marginBottom: 32 }}>
        {/* Kolom Absen Masuk */}
        <div className="card" style={{ flex: 1 }}>
          <h3>🟢 Absen Masuk</h3>
          {todayAbsensi && todayAbsensi.status ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Sudah Absen Masuk</h2>
              <div className={`status-badge status-${todayAbsensi.status}`}>{todayAbsensi.status?.toUpperCase()}</div>
              {todayAbsensi.foto && <img src={todayAbsensi.foto} alt="Foto Masuk" width={80} style={{ marginTop: 10, borderRadius: 8 }} />}
            </div>
          ) : (
            <>
              {messageMasuk && (
                <div className={`alert ${messageMasuk.includes('❌') ? 'alert-error' : 'alert-success'}`}>{messageMasuk}</div>
              )}
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keteranganMasuk}
                  onChange={e => setKeteranganMasuk(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>
              <div className="form-group">
                {!fotoMasuk && !showCameraMasuk && (
                  <button onClick={() => setShowCameraMasuk(true)} style={{ marginTop: 10, background: 'blue', color: 'white', padding: '8px 15px', borderRadius: 5 }}>📷 Buka Kamera</button>
                )}
                {showCameraMasuk && (
                  <div style={{ marginTop: 10 }}>
                    <Webcam
                      audio={false}
                      ref={webcamRefMasuk}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button onClick={capturePhotoMasuk} style={{ marginTop: 10, background: 'green', color: 'white', padding: '8px 15px', borderRadius: 5 }}>📸 Ambil Foto</button>
                  </div>
                )}
                {fotoMasuk && (
                  <div style={{ marginTop: 10 }}>
                    <img src={fotoMasuk} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: 8 }} />
                    <br />
                    <button onClick={() => setFotoMasuk(null)} style={{ marginTop: 10, background: 'gray', color: 'white', padding: '6px 12px', borderRadius: 5 }}>🔄 Ulangi Foto</button>
                  </div>
                )}
              </div>
              <button onClick={handleAbsensiMasuk} className="btn btn-success" disabled={loadingMasuk || !fotoMasuk} style={{ marginTop: 16 }}>✅ Absen Masuk</button>
            </>
          )}
        </div>
        {/* Kolom Absen Pulang */}
        <div className="card" style={{ flex: 1 }}>
          <h3>🔴 Absen Pulang</h3>
          {todayAbsensi && todayAbsensi.pulang_status ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Sudah Absen Pulang</h2>
              <div className={`status-badge status-${todayAbsensi.pulang_status}`}>{todayAbsensi.pulang_status?.toUpperCase()}</div>
              {todayAbsensi.pulang_foto && <img src={todayAbsensi.pulang_foto} alt="Foto Pulang" width={80} style={{ marginTop: 10, borderRadius: 8 }} />}
            </div>
          ) : (
            <>
              {messagePulang && (
                <div className={`alert ${messagePulang.includes('❌') ? 'alert-error' : 'alert-success'}`}>{messagePulang}</div>
              )}
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keteranganPulang}
                  onChange={e => setKeteranganPulang(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>
              <div className="form-group">
                {!fotoPulang && !showCameraPulang && (
                  <button onClick={() => setShowCameraPulang(true)} style={{ marginTop: 10, background: 'blue', color: 'white', padding: '8px 15px', borderRadius: 5 }}>📷 Buka Kamera</button>
                )}
                {showCameraPulang && (
                  <div style={{ marginTop: 10 }}>
                    <Webcam
                      audio={false}
                      ref={webcamRefPulang}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button onClick={capturePhotoPulang} style={{ marginTop: 10, background: 'green', color: 'white', padding: '8px 15px', borderRadius: 5 }}>📸 Ambil Foto</button>
                  </div>
                )}
                {fotoPulang && (
                  <div style={{ marginTop: 10 }}>
                    <img src={fotoPulang} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: 8 }} />
                    <br />
                    <button onClick={() => setFotoPulang(null)} style={{ marginTop: 10, background: 'gray', color: 'white', padding: '6px 12px', borderRadius: 5 }}>🔄 Ulangi Foto</button>
                  </div>
                )}
              </div>
              <button onClick={handleAbsensiPulang} className="btn btn-danger" disabled={loadingPulang || !fotoPulang || !(todayAbsensi && todayAbsensi.status)} style={{ marginTop: 16 }}>⏹️ Absen Pulang</button>
              {!(todayAbsensi && todayAbsensi.status) && <div style={{ color: 'red', marginTop: 8 }}>* Absen masuk dulu sebelum absen pulang</div>}
            </>
          )}
        </div>
      </div>

      {/* Riwayat Absensi */}
      <div className="card">
        <h3>📋 Riwayat Absensi (7 Hari Terakhir)</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Tanggal</th>
              <th>Status Masuk</th>
              <th>Status Pulang</th>
              <th>Keterangan Masuk</th>
              <th>Keterangan Pulang</th>
              <th>Foto Masuk</th>
              <th>Foto Pulang</th>
            </tr>
          </thead>
          <tbody>
            {getRecentAbsensi().map((abs) => (
              <tr key={abs.id}>
                <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                <td>{abs.status ? abs.status.toUpperCase() : '-'}</td>
                <td>{abs.pulang_status ? abs.pulang_status.toUpperCase() : '-'}</td>
                <td>{abs.keterangan || '-'}</td>
                <td>{abs.pulang_keterangan || '-'}</td>
                <td>{abs.foto && <img src={abs.foto} alt="Masuk" width={50} height={50} />}</td>
                <td>{abs.pulang_foto && <img src={abs.pulang_foto} alt="Pulang" width={50} height={50} />}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DashboardUser;
