      return false;
    }
  };

  const logout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    setSiswaData([]);
    setAbsensiData([]);
  };

  const addSiswa = async (siswa) => {
    try {
      const response = await fetch(`${API_URL}/signup`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: siswa.nis, // gunakan NIS sebagai username
          password: 'default123', // password default
          name: siswa.name,
          nis: siswa.nis,
          kelas: siswa.kelas
        })
      });

      if (response.ok) {
        fetchUsers(); // refresh data
        return true;
      } else {
        const data = await response.json();
        alert(data.error);
        return false;
      }
    } catch (error) {
      console.error('Add siswa error:', error);
      alert('Gagal menambah siswa');
      return false;
    }
  };

  const addAbsensi = async (absensi) => {
    try {
      const response = await fetch(`${API_URL}/absensi`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(absensi)
      });

      const data = await response.json();

      if (response.ok) {
        fetchAbsensi(); // refresh data absensi
        return true;
      } else {
        alert(data.error);
        return false;
      }
    } catch (error) {
      console.error('Add absensi error:', error);
      alert('Gagal menambah absensi');
      return false;
    }
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route 
          path="/login" 
          element={
            <Login 
              onLogin={login} 
              currentUser={currentUser}
              onSignUp={signUp}
            />
          } 
        />

        <Route 
          path="/" 
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route 
            index 
            element={
              currentUser?.role === 'admin' ? (
                <DashboardAdmin 
                  siswaData={siswaData}
                  absensiData={absensiData}
                  onAddSiswa={addSiswa}
                />
              ) : (
                <DashboardUser 
                  currentUser={currentUser}
                  absensiData={absensiData}
                  onAddAbsensi={addAbsensi}
                />
              )
            } 
          />

          <Route 
            path="/rekap" 
            element={
              <Rekap 
                siswaData={siswaData}
                absensiData={absensiData}
              />
            } 
          />

          <Route 
            path="/capture" 
            element={
              <Capture 
                currentUser={currentUser} 
                onSuccess={(payload) => {
                  fetchAbsensi(); // refresh absensi setelah capture
                }}
              />
            } 
          />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
