import React, { useState } from 'react';

const DashboardAdmin = ({ siswaData, absensiData, onAddSiswa }) => {
  const [formSiswa, setFormSiswa] = useState({
    name: '',
    nis: '',
    kelas: ''
  });
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');
  const [modalFoto, setModalFoto] = useState(null);

  const handleInputChange = (e) => {
    setFormSiswa({
      ...formSiswa,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddSiswa(formSiswa);
    setFormSiswa({ name: '', nis: '', kelas: '' });
    setShowForm(false);
    setMessage('Siswa berhasil ditambahkan!');
    setTimeout(() => setMessage(''), 3000);
  };

  const getAbsensiToday = () => {
    const today = new Date().toISOString().split('T')[0];
    return absensiData.filter(abs => abs.tanggal === today);
  };

  const getStatusCount = (status) => {
    return getAbsensiToday().filter(abs => abs.status === status).length;
  };

useEffect(() => {
  fetch("http://localhost:5000/absensi")
    .then(res => res.json())
    .then(data => setAbsensiData(data));
}, []);



  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üë®‚Äçüíº Dashboard Admin</h1>
        <p>Kelola data siswa dan pantau absensi harian</p>
      </div>

      {message && (
        <div className="alert alert-success">
          {message}
        </div>
      )}

        {/* Daftar Siswa */}
        <div className="card">
          <h3>üë• Daftar Siswa ({siswaData.length})</h3>
          
          <button 
            onClick={() => setShowForm(!showForm)}
            className="btn btn-primary"
            style={{ marginBottom: '20px' }}
          >
            {showForm ? '‚ùå Batal' : '‚ûï Tambah Siswa'}
          </button>

          {showForm && (
            <form onSubmit={handleSubmit} style={{ marginBottom: '20px', padding: '20px', background: '#f8f9fa', borderRadius: '10px' }}>
              <div className="form-inline">
                <div className="form-group">
                  <label>Nama Lengkap:</label>
                  <input
                    type="text"
                    name="name"
                    value={formSiswa.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Contoh: Ahmad Fauzi"
                  />
                </div>
                <div className="form-group">
                  <label>NIS:</label>
                  <input
                    type="text"
                    name="nis"
                    value={formSiswa.nis}
                    onChange={handleInputChange}
                    required
                    placeholder="Contoh: 2024001"
                  />
                </div>
                <div className="form-group">
                  <label>Kelas:</label>
                  <input
                    type="text"
                    name="kelas"
                    value={formSiswa.kelas}
                    onChange={handleInputChange}
                    required
                    placeholder="Contoh: XII RPL 1"
                  />
                </div>
                <div className="form-group">
                  <button type="submit" className="btn btn-success">
                    üíæ Simpan
                  </button>
                </div>
              </div>
            </form>
          )}

          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>No</th>
                  <th>NIS</th>
                  <th>Nama</th>
                  <th>Kelas</th>
                </tr>
              </thead>
              <tbody>
                {siswaData.map((siswa, index) => (
                  <tr key={siswa.id}>
                    <td>{index + 1}</td>
                    <td>{siswa.nis}</td>
                    <td>{siswa.name}</td>
                    <td>{siswa.kelas}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Tabel Absensi Semua Siswa */}
        <div className="card" style={{ marginTop: '30px' }}>
          <h3>üìã Daftar Absensi Siswa</h3>
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>No</th>
                  <th>Tanggal</th>
                  <th>NIS</th>
                  <th>Nama</th>
                  <th>Kelas</th>
                  <th>Status</th>
                  <th>Keterangan</th>
                  <th>Foto</th>
                </tr>
              </thead>
              <tbody>
  {absensiData.map((abs, index) => (
    <tr key={abs.id || index}>
      <td>{index + 1}</td>
      <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
      <td>{abs.nis}</td>
      <td>{abs.name}</td>
      <td>{abs.kelas}</td>
      <td>
        <span className={`status-badge status-${abs.status}`}>
          {abs.status.toUpperCase()}
        </span>
      </td>
      <td>{abs.keterangan || '-'}</td>
      <td>
        {abs.foto ? (
          <img src={abs.foto} alt="Absensi" width={40} height={40} />
        ) : (
          '-'
        )}
      </td>
    </tr>
  ))}
 
              </tbody>
            </table>
            {modalFoto && (
              <div
                style={{
                  position: 'fixed',
                  top: 0, left: 0, right: 0, bottom: 0,
                  background: 'rgba(0,0,0,0.7)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  zIndex: 9999
                }}
                onClick={() => setModalFoto(null)}
              >
                <img src={modalFoto} alt="Foto Besar" style={{ maxWidth: '90vw', maxHeight: '90vh', borderRadius: '12px', boxShadow: '0 0 20px #000' }} />
              </div>
            )}
          </div>
        </div>
      </div>
  );
};



export default DashboardAdmin;
