import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import ProtectedRoute from './components/ProtectedRoute';
import { dummyUsers, dummySiswa, dummyAbsensi } from './data/dummyData';
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [siswaData, setSiswaData] = useState(dummySiswa);
  const [absensiData, setAbsensiData] = useState(dummyAbsensi);

  const login = (username, password) => {
    const user = dummyUsers.find(u => u.username === username && u.password === password);
    if (user) {
      setCurrentUser(user);
      return true;
    }
    return false;
  };

  const logout = () => {
    setCurrentUser(null);
  };

  const addSiswa = (siswa) => {
    const newSiswa = {
      id: siswaData.length + 1,
      ...siswa
    };
    setSiswaData([...siswaData, newSiswa]);
  };

  const addAbsensi = (absensi) => {
    const newAbsensi = {
      id: absensiData.length + 1,
      ...absensi,
      tanggal: new Date().toISOString().split('T')[0]
    };
    setAbsensiData([...absensiData, newAbsensi]);
  };

  return (
    <Router>
      <Routes>
        <Route 
          path="/login" 
          element={
            <Login 
              onLogin={login} 
              currentUser={currentUser} 
            />
          } 
        />
        <Route 
          path="/" 
          element={
            <ProtectedRoute currentUser={currentUser}>
              <Layout currentUser={currentUser} onLogout={logout} />
            </ProtectedRoute>
          }
        >
          <Route 
            index 
            element={
              currentUser?.role === 'admin' ? (
                <DashboardAdmin 
                  siswaData={siswaData}
                  absensiData={absensiData}
                  onAddSiswa={addSiswa}
                />
              ) : (
                <DashboardUser 
                  currentUser={currentUser}
                  absensiData={absensiData}
                  onAddAbsensi={addAbsensi}
                />
              )
            } 
          />
          <Route 
            path="/rekap" 
            element={
              <Rekap 
                siswaData={siswaData}
                absensiData={absensiData}
              />
            } 
          />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
