import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const hasAbsensiToday = absensiData.some(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const todayAbsensi = absensiData.find(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  // üì∏ Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false); // setelah ambil foto, kamera ditutup
  };

const handleAbsensi = async (status) => {
  const response = await fetch("http://localhost:5000/absensi", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      user_id: currentUser.id,
      status,
      keterangan,
      foto
    }),
  });

  const data = await response.json();
  if (response.ok) {
    alert("‚úÖ " + data.message);
  } else {
    alert("‚ùå Gagal absen: " + data.error);
  }
};

 const getRecentAbsensi = () => {
  return absensiData
    .filter(abs => abs.user_id === currentUser.id) // user_id bukan siswaId
    .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
    .slice(0, 7);
};

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üëã Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>üìÖ {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('‚ùå') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>‚úèÔ∏è Absensi Hari Ini</h3>
          
          {hasAbsensiToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Absensi Sudah Tercatat</h2>
              <div className={`status-badge status-${todayAbsensi?.status}`}>
                {todayAbsensi?.status?.toUpperCase()}
              </div>
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    style={{ marginTop: '10px', background: 'blue', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                  >
                    üì∑ Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div style={{ marginTop: '10px' }}>
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                    >
                      üì∏ Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                    <br />
                    <button 
                      onClick={() => setFoto(null)} 
                      style={{ marginTop: '10px', background: 'gray', color: 'white', padding: '6px 12px', borderRadius: '5px' }}
                    >
                      üîÑ Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons">
                <button onClick={() => handleAbsensi('hadir')} className="btn btn-success">‚úÖ HADIR</button>
                <button onClick={() => handleAbsensi('izin')} className="btn btn-warning">üìù IZIN</button>
                <button onClick={() => handleAbsensi('sakit')} className="btn btn-danger">ü§í SAKIT</button>
              </div>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>üìã Riwayat Absensi (7 Hari Terakhir)</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getRecentAbsensi().map((abs) => (
                <tr key={abs.id}>
                  <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                  <td>{abs.status.toUpperCase()}</td>
                  <td>{abs.keterangan || '-'}</td>
                  <td>
                    {abs.foto && <img src={abs.foto} alt="Absensi" width={50} height={50} />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
