import React, { useState, useRef } from 'react';
import Webcam from "react-webcam";

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [message, setMessage] = useState('');
  const webcamRef = useRef(null);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const hasAbsensiToday = absensiData.some(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const todayAbsensi = absensiData.find(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  // 📸 Ambil foto dari kamera
  const capturePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFoto(imageSrc);
    setShowCamera(false); // setelah ambil foto, kamera ditutup
  };

  const handleAbsensi = (status) => {
    if (hasAbsensiToday) {
      setMessage('❌ Anda sudah melakukan absensi hari ini!');
      return;
    }
    if (!foto) {
      setMessage('❌ Silakan ambil foto terlebih dahulu!');
      return;
    }

     const absensiDataObj = {
    id: Date.now(), // id unik
    siswaId: currentUser.id,  // 🔑 pastikan ini ikut
    nis: currentUser.nis,     // 🔑 simpan NIS juga
    name: currentUser.name,   // 🔑 simpan nama user yang login
    kelas: currentUser.kelas, // 🔑 simpan kelas
    tanggal: new Date().toISOString().split('T')[0],
    status: status,
    keterangan: keterangan,
    foto: foto
  };

    onAddAbsensi(absensiDataObj);
    setMessage(`✅ Absensi berhasil! Status: ${status.toUpperCase()}`);
    setKeterangan('');
    setFoto(null);
    setTimeout(() => setMessage(''), 5000);
  };

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.siswaId === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>👋 Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>📅 {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('❌') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>✏️ Absensi Hari Ini</h3>
          
          {hasAbsensiToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Absensi Sudah Tercatat</h2>
              <div className={`status-badge status-${todayAbsensi?.status}`}>
                {todayAbsensi?.status?.toUpperCase()}
              </div>
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!foto && !showCamera && (
                  <button 
                    onClick={() => setShowCamera(true)} 
                    style={{ marginTop: '10px', background: 'blue', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                  >
                    📷 Buka Kamera
                  </button>
                )}

                {showCamera && (
                  <div style={{ marginTop: '10px' }}>
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={250}
                      videoConstraints={{ facingMode: "user" }}
                    />
                    <br />
                    <button 
                      onClick={capturePhoto} 
                      style={{ marginTop: '10px', background: 'green', color: 'white', padding: '8px 15px', borderRadius: '5px' }}
                    >
                      📸 Ambil Foto
                    </button>
                  </div>
                )}

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                    <br />
                    <button 
                      onClick={() => setFoto(null)} 
                      style={{ marginTop: '10px', background: 'gray', color: 'white', padding: '6px 12px', borderRadius: '5px' }}
                    >
                      🔄 Ulangi Foto
                    </button>
                  </div>
                )}
              </div>

              {/* Tombol Absensi */}
              <div className="absensi-buttons">
                <button onClick={() => handleAbsensi('hadir')} className="btn btn-success">✅ HADIR</button>
                <button onClick={() => handleAbsensi('izin')} className="btn btn-warning">📝 IZIN</button>
                <button onClick={() => handleAbsensi('sakit')} className="btn btn-danger">🤒 SAKIT</button>
              </div>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>📋 Riwayat Absensi (7 Hari Terakhir)</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Tanggal</th>
                <th>Status</th>
                <th>Keterangan</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {getRecentAbsensi().map((abs) => (
                <tr key={abs.id}>
                  <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                  <td>{abs.status.toUpperCase()}</td>
                  <td>{abs.keterangan || '-'}</td>
                  <td>
                    {abs.foto && <img src={abs.foto} alt="Absensi" width={50} height={50} />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
