import React, { useState, useRef } from 'react';

const DashboardUser = ({ currentUser, absensiData, onAddAbsensi }) => {
  const [keterangan, setKeterangan] = useState('');
  const [foto, setFoto] = useState(null);
  const [message, setMessage] = useState('');
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraOn, setIsCameraOn] = useState(false);

  const today = new Date().toISOString().split('T')[0];
  const todayFormatted = new Date().toLocaleDateString('id-ID', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const hasAbsensiToday = absensiData.some(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  const todayAbsensi = absensiData.find(
    abs => abs.siswaId === currentUser.id && abs.tanggal === today
  );

  // Nyalakan kamera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsCameraOn(true);
      }
    } catch (err) {
      console.error("Gagal mengakses kamera:", err);
      setMessage("‚ùå Kamera tidak bisa diakses. Cek izin browser.");
    }
  };

  // Ambil foto dari video
  const capturePhoto = () => {
    if (videoRef.current && canvasRef.current) {
      const context = canvasRef.current.getContext("2d");
      context.drawImage(videoRef.current, 0, 0, 200, 200);
      const imageData = canvasRef.current.toDataURL("image/png");
      setFoto(imageData);

      // Matikan kamera setelah capture
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
      setIsCameraOn(false);
    }
  };

  const handleAbsensi = (status) => {
    if (hasAbsensiToday) {
      setMessage('‚ùå Anda sudah melakukan absensi hari ini!');
      return;
    }
    if (!foto) {
      setMessage('‚ùå Silakan ambil foto terlebih dahulu!');
      return;
    }

    const absensiDataObj = {
      siswaId: currentUser.id,
      status: status,
      keterangan: keterangan,
      foto: foto
    };

    onAddAbsensi(absensiDataObj);
    setMessage(`‚úÖ Absensi berhasil! Status: ${status.toUpperCase()}`);
    setKeterangan('');
    setFoto(null);
    setTimeout(() => setMessage(''), 5000);
  };

  const getRecentAbsensi = () => {
    return absensiData
      .filter(abs => abs.siswaId === currentUser.id)
      .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
      .slice(0, 7);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üëã Selamat Datang, {currentUser.name}!</h1>
        <p>NIS: {currentUser.nis} | Kelas: {currentUser.kelas}</p>
        <p>üìÖ {todayFormatted}</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('‚ùå') ? 'alert-error' : 'alert-success'}`}>
          {message}
        </div>
      )}

      <div className="dashboard-content">
        {/* Form Absensi */}
        <div className="card">
          <h3>‚úèÔ∏è Absensi Hari Ini</h3>

          {hasAbsensiToday ? (
            <div style={{ textAlign: 'center', padding: '20px' }}>
              <h2>Absensi Sudah Tercatat</h2>
            </div>
          ) : (
            <div>
              <div className="form-group">
                <label>Keterangan (Opsional):</label>
                <textarea
                  value={keterangan}
                  onChange={(e) => setKeterangan(e.target.value)}
                  placeholder="Masukkan keterangan jika diperlukan..."
                  style={{
                    width: '100%',
                    minHeight: '80px',
                    padding: '12px',
                    border: '2px solid #ddd',
                    borderRadius: '5px',
                    resize: 'vertical'
                  }}
                />
              </div>

              {/* Kamera */}
              <div className="form-group">
                {!isCameraOn && !foto && (
                  <button onClick={startCamera} className="btn btn-primary">üì∑ Nyalakan Kamera</button>
                )}

                {isCameraOn && (
                  <div>
                    <video ref={videoRef} autoPlay style={{ width: "200px", borderRadius: "10px" }}></video>
                    <br />
                    <button onClick={capturePhoto} className="btn btn-success mt-2">üì∏ Ambil Foto</button>
                  </div>
                )}

                <canvas ref={canvasRef} width={200} height={200} style={{ display: "none" }}></canvas>

                {foto && (
                  <div style={{ marginTop: '10px' }}>
                    <img src={foto} alt="Preview" width={120} height={120} style={{ objectFit: 'cover', borderRadius: '8px' }} />
                  </div>
                )}
              </div>

              <div className="absensi-buttons">
                <button onClick={() => handleAbsensi('hadir')} className="btn btn-success">‚úÖ HADIR</button>
                <button onClick={() => handleAbsensi('izin')} className="btn btn-warning">üìù IZIN</button>
                <button onClick={() => handleAbsensi('sakit')} className="btn btn-danger">ü§í SAKIT</button>
              </div>
            </div>
          )}
        </div>

        {/* Riwayat Absensi */}
        <div className="card">
          <h3>üìã Riwayat Absensi (7 Hari Terakhir)</h3>
          {getRecentAbsensi().length === 0 ? (
            <p style={{ textAlign: 'center', color: '#666', fontStyle: 'italic' }}>Belum ada riwayat absensi</p>
          ) : (
            <div className="table-container">
              <table className="table">
                <thead>
                  <tr>
                    <th>Tanggal</th>
                    <th>Status</th>
                    <th>Keterangan</th>
                    <th>Foto</th>
                  </tr>
                </thead>
                <tbody>
                  {getRecentAbsensi().map((abs) => (
                    <tr key={abs.id}>
                      <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                      <td>{abs.status.toUpperCase()}</td>
                      <td>{abs.keterangan || '-'}</td>
                      <td>
                        {abs.foto && (
                          <img src={abs.foto} alt="Absensi" width={40} height={40} style={{ objectFit: 'cover', borderRadius: '6px' }} />
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardUser;
