import React, { useState, useEffect } from 'react';

const DashboardAdmin = ({ siswaData, absensiData, onAddSiswa }) => {
  const [formSiswa, setFormSiswa] = useState({
    name: '',
    nis: '',
    kelas: ''
  });
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');
  const [modalFoto, setModalFoto] = useState(null);
  const [activeTab, setActiveTab] = useState('all');
  const [refreshKey, setRefreshKey] = useState(0);

  // Refresh data setiap 5 detik
  useEffect(() => {
    const interval = setInterval(() => {
      setRefreshKey(prev => prev + 1);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleInputChange = (e) => {
    setFormSiswa({
      ...formSiswa,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await onAddSiswa(formSiswa);
    if (result) {
      setFormSiswa({ name: '', nis: '', kelas: '' });
      setShowForm(false);
      setMessage('âœ… Siswa berhasil ditambahkan!');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const today = new Date().toISOString().split('T')[0];

  // Filter siswa berdasarkan kelas
  const getFilteredSiswa = () => {
    if (!siswaData) return [];
    switch (activeTab) {
      case 'xii-rpl1':
        return siswaData.filter(siswa => siswa.kelas === 'XII RPL 1');
      case 'xii-rpl2':
        return siswaData.filter(siswa => siswa.kelas === 'XII RPL 2');
      case 'xii-rpl3':
        return siswaData.filter(siswa => siswa.kelas === 'XII RPL 3');
      default:
        return siswaData;
    }
  };

  // Filter absensi berdasarkan kelas
  const getFilteredAbsensi = () => {
    if (!absensiData) return [];
    switch (activeTab) {
      case 'xii-rpl1':
        return absensiData.filter(abs => abs.kelas === 'XII RPL 1');
      case 'xii-rpl2':
        return absensiData.filter(abs => abs.kelas === 'XII RPL 2');
      case 'xii-rpl3':
        return absensiData.filter(abs => abs.kelas === 'XII RPL 3');
      default:
        return absensiData;
    }
  };

  // Ambil absensi hari ini
  const getAbsensiToday = () => {
    return getFilteredAbsensi().filter(abs => abs.tanggal === today);
  };

  // Hitung status berdasarkan jenis absensi
  const getStatusCount = (status) => {
    const absensiToday = getAbsensiToday();
    return absensiToday.filter(abs => abs.status === status).length;
  };

  // Cek apakah siswa sudah absen hari ini
  const getSiswaAbsensiStatus = (siswaId) => {
    const absensiToday = getAbsensiToday();
    const absenMasuk = absensiToday.find(
      abs => abs.user_id === siswaId && abs.jenis_absensi === 'masuk'
    );
    const absenPulang = absensiToday.find(
      abs => abs.user_id === siswaId && abs.jenis_absensi === 'pulang'
    );

    if (absenMasuk && absenPulang) {
      return {
        status: 'complete',
        label: 'âœ… LENGKAP',
        color: '#28a745',
        detail: `${absenMasuk.status.toUpperCase()} (Masuk & Pulang)`
      };
    } else if (absenMasuk) {
      return {
        status: 'partial',
        label: 'âš ï¸ MASUK SAJA',
        color: '#ffc107',
        detail: absenMasuk.status.toUpperCase()
      };
    } else {
      return {
        status: 'absent',
        label: 'âŒ BELUM ABSEN',
        color: '#dc3545',
        detail: 'Belum mengisi absensi'
      };
    }
  };

  // Kategorisasi siswa
  const categorizeSiswa = () => {
    const filtered = getFilteredSiswa();
    const sudahAbsen = [];
    const belumAbsen = [];

    filtered.forEach(siswa => {
      const status = getSiswaAbsensiStatus(siswa.id);
      if (status.status === 'absent') {
        belumAbsen.push(siswa);
      } else {
        sudahAbsen.push(siswa);
      }
    });

    return { sudahAbsen, belumAbsen };
  };

  const filteredSiswa = getFilteredSiswa();
  const filteredAbsensi = getFilteredAbsensi();
  const { sudahAbsen, belumAbsen } = categorizeSiswa();

  // Helper untuk judul tab
  const getTabTitle = () => {
    switch (activeTab) {
      case 'xii-rpl1': return '(XII RPL 1)';
      case 'xii-rpl2': return '(XII RPL 2)';
      case 'xii-rpl3': return '(XII RPL 3)';
      default: return '(Semua Kelas)';
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>ğŸ‘¨â€ğŸ’¼ Dashboard Admin</h1>
        <p>Kelola data siswa dan pantau absensi harian</p>
        <p style={{ fontSize: '14px', color: '#666' }}>
          ğŸ“… {new Date().toLocaleDateString('id-ID', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          })}
        </p>
      </div>

      {message && (
        <div className="alert alert-success">
          {message}
        </div>
      )}

      {/* Tabs Navigation */}
      <div className="tabs-container" style={{ marginBottom: '20px' }}>
        <div className="tabs" style={{ display: 'flex', gap: '10px', borderBottom: '2px solid #e9ecef', flexWrap: 'wrap' }}>
          <button
            onClick={() => setActiveTab('all')}
            style={{
              padding: '12px 20px',
              border: 'none',
              background: activeTab === 'all' ? '#007bff' : 'transparent',
              color: activeTab === 'all' ? 'white' : '#666',
              borderRadius: '8px 8px 0 0',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'all 0.3s ease'
            }}
          >
            ğŸ“Š Semua Kelas
          </button>
          <button
            onClick={() => setActiveTab('xii-rpl1')}
            style={{
              padding: '12px 20px',
              border: 'none',
              background: activeTab === 'xii-rpl1' ? '#28a745' : 'transparent',
              color: activeTab === 'xii-rpl1' ? 'white' : '#666',
              borderRadius: '8px 8px 0 0',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'all 0.3s ease'
            }}
          >
            ğŸ‘¥ XII RPL 1
          </button>
          <button
            onClick={() => setActiveTab('xii-rpl2')}
            style={{
              padding: '12px 20px',
              border: 'none',
              background: activeTab === 'xii-rpl2' ? '#ffc107' : 'transparent',
              color: activeTab === 'xii-rpl2' ? '#212529' : '#666',
              borderRadius: '8px 8px 0 0',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'all 0.3s ease'
            }}
          >
            ğŸ‘¥ XII RPL 2
          </button>
          <button
            onClick={() => setActiveTab('xii-rpl3')}
            style={{
              padding: '12px 20px',
              border: 'none',
              background: activeTab === 'xii-rpl3' ? '#dc3545' : 'transparent',
              color: activeTab === 'xii-rpl3' ? 'white' : '#666',
              borderRadius: '8px 8px 0 0',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'all 0.3s ease'
            }}
          >
            ğŸ‘¥ XII RPL 3
          </button>
        </div>
      </div>

      {/* Statistik Absensi Hari Ini */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>ğŸ“ˆ Statistik Absensi Hari Ini {getTabTitle()}</h3>
        <div style={{ display: 'flex', gap: '15px', marginTop: '15px', flexWrap: 'wrap' }}>
          <div style={{ textAlign: 'center', padding: '15px', background: '#28a745', color: 'white', borderRadius: '8px', flex: '1 1 200px', minWidth: '150px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('hadir')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>âœ… Hadir</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#ffc107', color: '#212529', borderRadius: '8px', flex: '1 1 200px', minWidth: '150px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('izin')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>ğŸ“ Izin</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#dc3545', color: 'white', borderRadius: '8px', flex: '1 1 200px', minWidth: '150px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{getStatusCount('sakit')}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>ğŸ¤’ Sakit</p>
          </div>
          <div style={{ textAlign: 'center', padding: '15px', background: '#6c757d', color: 'white', borderRadius: '8px', flex: '1 1 200px', minWidth: '150px' }}>
            <h4 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{belumAbsen.length}</h4>
            <p style={{ margin: 0, fontSize: '14px' }}>âŒ Belum Absen</p>
          </div>
        </div>
      </div>

      {/* Daftar Siswa Sudah Absen */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>âœ… Siswa Sudah Absen {getTabTitle()} ({sudahAbsen.length} siswa)</h3>
        
        <div className="table-container" style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Status Absensi</th>
                <th>Detail</th>
              </tr>
            </thead>
            <tbody>
              {sudahAbsen.length > 0 ? (
                sudahAbsen.map((siswa, index) => {
                  const statusInfo = getSiswaAbsensiStatus(siswa.id);
                  return (
                    <tr key={siswa.id}>
                      <td>{index + 1}</td>
                      <td>{siswa.nis}</td>
                      <td>{siswa.name}</td>
                      <td>{siswa.kelas}</td>
                      <td>
                        <span style={{ 
                          padding: '6px 12px', 
                          borderRadius: '4px', 
                          fontSize: '12px',
                          fontWeight: 'bold',
                          backgroundColor: statusInfo.color,
                          color: 'white',
                          display: 'inline-block'
                        }}>
                          {statusInfo.label}
                        </span>
                      </td>
                      <td style={{ fontSize: '13px', color: '#666' }}>
                        {statusInfo.detail}
                      </td>
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center', padding: '20px', color: '#999' }}>
                    Belum ada siswa yang absen hari ini
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Daftar Siswa Belum Absen */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h3>âŒ Siswa Belum Absen {getTabTitle()} ({belumAbsen.length} siswa)</h3>
        
        <button 
          onClick={() => setShowForm(!showForm)}
          className="btn btn-primary"
          style={{ marginBottom: '20px' }}
        >
          {showForm ? 'âŒ Batal' : 'â• Tambah Siswa'}
        </button>

        {showForm && (
          <form onSubmit={handleSubmit} style={{ marginBottom: '20px', padding: '20px', background: '#f8f9fa', borderRadius: '10px' }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
              <div className="form-group">
                <label>Nama Lengkap:</label>
                <input
                  type="text"
                  name="name"
                  value={formSiswa.name}
                  onChange={handleInputChange}
                  required
                  placeholder="Contoh: Ahmad Fauzi"
                />
              </div>
              <div className="form-group">
                <label>NIS:</label>
                <input
                  type="text"
                  name="nis"
                  value={formSiswa.nis}
                  onChange={handleInputChange}
                  required
                  placeholder="Contoh: 2024001"
                />
              </div>
              <div className="form-group">
                <label>Kelas:</label>
                <select
                  name="kelas"
                  value={formSiswa.kelas}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '8px', borderRadius: '4px', border: '1px solid #ccc' }}
                >
                  <option value="">Pilih Kelas</option>
                  <option value="XII RPL 1">XII RPL 1</option>
                  <option value="XII RPL 2">XII RPL 2</option>
                  <option value="XII RPL 3">XII RPL 3</option>
                </select>
              </div>
            </div>
            <button type="submit" className="btn btn-success" style={{ marginTop: '15px' }}>
              ğŸ’¾ Simpan
            </button>
          </form>
        )}

        <div className="table-container" style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {belumAbsen.length > 0 ? (
                belumAbsen.map((siswa, index) => (
                  <tr key={siswa.id}>
                    <td>{index + 1}</td>
                    <td>{siswa.nis}</td>
                    <td>{siswa.name}</td>
                    <td>{siswa.kelas}</td>
                    <td>
                      <span style={{ color: '#dc3545', fontWeight: 'bold' }}>
                        âŒ BELUM ABSEN
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" style={{ textAlign: 'center', padding: '20px', color: '#28a745', fontWeight: 'bold' }}>
                    âœ… Semua siswa sudah absen!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Tabel Riwayat Absensi Lengkap */}
      <div className="card" style={{ marginTop: '30px' }}>
        <h3>ğŸ“‹ Riwayat Absensi Lengkap {getTabTitle()}</h3>
        <div className="table-container" style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>No</th>
                <th>Tanggal</th>
                <th>NIS</th>
                <th>Nama</th>
                <th>Kelas</th>
                <th>Jenis</th>
                <th>Status</th>
                <th>Foto</th>
              </tr>
            </thead>
            <tbody>
              {filteredAbsensi.length > 0 ? (
                filteredAbsensi
                  .sort((a, b) => new Date(b.tanggal) - new Date(a.tanggal))
                  .map((abs, index) => (
                    <tr key={abs.id || index}>
                      <td>{index + 1}</td>
                      <td>{new Date(abs.tanggal).toLocaleDateString('id-ID')}</td>
                      <td>{abs.nis}</td>
                      <td>{abs.name}</td>
                      <td>{abs.kelas}</td>
                      <td>
                        <span style={{ 
                          padding: '4px 8px', 
                          borderRadius: '4px', 
                          fontSize: '12px',
                          backgroundColor: abs.jenis_absensi === 'masuk' ? '#007bff' : '#6c757d',
                          color: 'white',
                          fontWeight: 'bold'
                        }}>
                          {abs.jenis_absensi?.toUpperCase() || 'MASUK'}
                        </span>
                      </td>
                      <td>
                        <span style={{ 
                          padding: '4px 8px', 
                          borderRadius: '4px', 
                          fontSize: '12px',
                          fontWeight: 'bold',
                          backgroundColor: 
                            abs.status === 'hadir' ? '#28a745' : 
                            abs.status === 'izin' ? '#ffc107' : '#dc3545',
                          color: abs.status === 'izin' ? '#212529' : 'white'
                        }}>
                          {abs.status.toUpperCase()}
                        </span>
                      </td>
                      <td>
                        {abs.foto ? (
                          <img 
                            src={abs.foto} 
                            alt="Absensi" 
                            width={40} 
                            height={40} 
                            style={{ cursor: 'pointer', borderRadius: '4px', objectFit: 'cover' }}
                            onClick={() => setModalFoto(abs.foto)}
                          />
                        ) : (
                          '-'
                        )}
                      </td>
                    </tr>
                  ))
              ) : (
                <tr>
                  <td colSpan="8" style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
                    Tidak ada data absensi untuk {getTabTitle()}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal Foto */}
      {modalFoto && (
        <div
          style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
            cursor: 'pointer'
          }}
          onClick={() => setModalFoto(null)}
        >
          <img 
            src={modalFoto} 
            alt="Foto Absensi" 
            style={{ 
              maxWidth: '90vw', 
              maxHeight: '90vh', 
              borderRadius: '12px', 
              boxShadow: '0 0 30px rgba(0,0,0,0.5)',
              border: '3px solid white'
            }} 
          />
        </div>
      )}
    </div>
  );
};

export default DashboardAdmin;
